import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BotaoAcao from './BotaoAcao';
import Filtro from './FiltroEnviadas'; // Assuming this component is consistent

function Publicadas({ receitas }) {
  const navigate = useNavigate();

  const [filtros, setFiltros] = useState({
    status: '', // This might not be strictly needed for 'Publicadas' as they are all 'aprovado'
    id_categoria: '',
    id_epoca: '',
    id_solo: '',
    id_sol: ''
  });

  const aplicarFiltros = (f) => {
    setFiltros(f);
  };

  // Filter for approved recipes first (should already be the case for 'Publicadas')
  const receitasAprovadas = (receitas || []).filter(r => r.status === 'aprovado');

  // Apply additional filters from the 'Filtro' component
  const receitasFiltradas = receitasAprovadas.filter(r => {
    const filtroCategoria = filtros.id_categoria ? r.id_categoria == filtros.id_categoria : true;
    const filtroEpoca = filtros.id_epoca ? r.id_epoca == filtros.id_epoca : true;
    const filtroSolo = filtros.id_solo ? r.id_solo == filtros.id_solo : true;
    const filtroSol = filtros.id_sol ? r.id_sol == filtros.id_sol : true;

    return filtroCategoria && filtroEpoca && filtroSolo && filtroSol;
  });

  const styles = {
    container: {
      display: 'flex',
      gap: '20px',
      padding: '20px',
      minHeight: '100vh',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#f0f4f7', // Slightly lighter background
      width: '85%', // Slightly wider container
      margin: '0 auto',
    },
    filtrosCol: {
      flexShrink: 0,
      width: '280px', // Slightly wider filter column
      backgroundColor: '#fff',
      borderRadius: '12px',
      padding: '20px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.08)', // Stronger, softer shadow
      height: 'fit-content',
      position: 'sticky',
      top: '20px',
    },
    conteudo: {
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
    },
    receitaListaGrid: {
      display: 'grid',
      // Adjusted to fit 3-4 columns based on minmax for better responsiveness
      gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
      gap: '20px',
      alignContent: 'flex-start', // Align items to the start of the grid
    },
    receitaCard: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)', // Consistent shadow
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
      minHeight: '380px', // Slightly adjusted min-height for consistency
      transition: 'transform 0.2s ease-in-out, box-shadow 0.2s ease-in-out',
      '&:hover': {
        transform: 'translateY(-5px)',
        boxShadow: '0 6px 16px rgba(0, 0, 0, 0.12)',
      },
    },
    receitaImagemContainer: {
      width: '100%',
      height: '180px',
      overflow: 'hidden',
      backgroundColor: '#2d6a4f', // Green background for no image, consistent with moderation
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      flexShrink: 0,
      flexDirection: 'column',
      color: '#fff',
      fontWeight: 600,
      textAlign: 'center',
    },
    receitaImagem: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
    },
    noImageIcon: {
      width: '45px', // Slightly larger icon
      height: '45px',
      marginBottom: '10px',
      filter: 'brightness(0) invert(1)', // Make it white
    },
    noImageText: {
      margin: 0,
      fontSize: '1.1rem',
      fontWeight: 'bold'
    },
    receitaInfo: {
      padding: '15px',
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between', // Push buttons to bottom
    },
    receitaNome: {
      fontSize: '1.25rem', // Slightly larger title
      fontWeight: 700,
      color: '#333',
      margin: '0 0 10px 0',
      lineHeight: '1.3',
      textAlign: 'left', // Align text to left
    },
    receitaDetalhe: { // Unified style for author and date
      fontSize: '0.95rem',
      color: '#666',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      marginBottom: '8px',
      textAlign: 'left', // Align text to left
    },
    infoIcon: {
      width: '18px', // Slightly larger icons
      height: '18px',
      opacity: 0.7, // Slightly faded icons
    },
    botoesContainer: {
      padding: '15px 0 0', // Padding top only
      display: 'flex',
      justifyContent: 'flex-start', // Align buttons to start
      marginTop: 'auto',
      borderTop: '1px solid #eee', // Subtle separator
      paddingTop: '15px',
    },
    emptyStateContainer: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
      padding: '40px 20px',
      textAlign: 'center',
      maxWidth: '600px',
      margin: '80px auto',
    },
    emptyStateTitle: {
      fontSize: '1.5rem',
      fontWeight: 600,
      color: '#4a4a4a',
      marginBottom: '10px',
    },
    emptyStateText: {
      fontSize: '1rem',
      color: '#777',
      maxWidth: '400px',
      margin: '0 auto',
    },
  };

  return (
    <div style={styles.container}>
      {/* Coluna de Filtros */}
      <div style={styles.filtrosCol}>
        <Filtro onFiltrar={aplicarFiltros} />
      </div>

      {/* Conteúdo (lista de cards) */}
      <div style={styles.conteudo}>
        <div style={styles.receitaListaGrid}>
          {receitasFiltradas.length === 0 ? (
            <div style={styles.emptyStateContainer}>
              <h3 style={styles.emptyStateTitle}>Nenhuma receita publicada ainda.</h3>
              <p style={styles.emptyStateText}>
                Receitas aprovadas aparecerão aqui para o público em geral.
              </p>
            </div>
          ) : (
            receitasFiltradas.map(r => {
              const dataFormatada = new Date(r.data_publicacao).toLocaleDateString('pt-BR');
              const hasImage = r.imagens?.[0];
              const idReceita = r.id || r.id_conteudo;

              return (
                <div key={idReceita} style={styles.receitaCard}>
                  <div style={styles.receitaImagemContainer}>
                    {hasImage ? (
                      <img
                        src={`http://localhost:5000${r.imagens[0]}`}
                        alt={r.nomePlanta}
                        style={styles.receitaImagem}
                      />
                    ) : (
                      <>
                        <img src="/alert-icon.png" alt="Sem imagem" style={styles.noImageIcon} /> {/* Assuming you have an alert icon */}
                        <p style={styles.noImageText}>Sem Imagem</p>
                      </>
                    )}
                </div>
                <div style={styles.receitaInfo}>
                  <h3 style={styles.receitaNome}>{r.nomePlanta || 'Sem Título'}</h3>
                  <p style={styles.receitaDetalhe}>
                    <img src="/user-solid.png" alt="Ícone de usuário" style={styles.infoIcon} /> {/* Changed to solid user icon for consistency if available */}
                    {r.autor || 'Anônimo'}
                  </p>
                  <p style={styles.receitaDetalhe}>
                    <img src="/calendar-days-solid.png" alt="Ícone de calendário" style={styles.infoIcon} /> {/* Changed to solid calendar icon if available */}
                    Publicado em: {dataFormatada}
                  </p>
                  <div style={styles.botoesContainer}>
                    <BotaoAcao
                      label="Ver receita"
                      tipo="detalhes" // Using 'detalhes' type for a primary view button, assuming it has a distinct style
