import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Topo({ comMenu }) {
  const [menuAberto, setMenuAberto] = useState(false);
  const navigate = useNavigate();

  const toggleMenu = () => setMenuAberto(!menuAberto);

  const irPara = (rota) => {
    navigate(rota);
    setMenuAberto(false);
  };

  const sair = () => {
    localStorage.removeItem('user'); // ou sessionStorage
    navigate('/');
  };

  const styles = {
    menuFixoEsquerda: {
      position: 'fixed',
      top: '12.5px', // Ajustado para centralizar o ícone na barra
      left: '15px',
      zIndex: 1000,
      display: 'flex',
      alignItems: 'center',
    },
    menuIcon: {
      width: '30px',
      height: '30px',
      cursor: 'pointer',
    },
    menuLateral: {
      position: 'absolute',
      top: '50px',
      left: 0,
      backgroundColor: '#fff',
      border: '1px solid #ccc',
      borderRadius: '8px',
      padding: '10px',
      boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
      zIndex: 999,
      display: 'flex',
      flexDirection: 'column',
      gap: '5px',
    },
    menuButton: {
      backgroundColor: 'transparent',
      border: 'none',
      padding: '8px',
      fontSize: '1rem',
      textAlign: 'left',
      cursor: 'pointer',
      transition: 'background-color 0.2s',
      borderRadius: '4px',
    },
    menuButtonHover: {
      backgroundColor: '#f0f0f0',
    },
    logoCentralTopo: {
      position: 'relative',
      width: '100%',
      textAlign: 'center',
      padding: '5px 0', // Diminui o padding vertical para diminuir a altura da barra
      backgroundColor: '#fff',
    },
    logoTopo: {
      height: '40px', // Diminui o tamanho do logo
    },
  };

  return (
    <>
      {/* Menu no canto superior esquerdo */}
      {comMenu && (
        <div style={styles.menuFixoEsquerda}>
          <img
            src="/menu.png"
            alt="Menu"
            style={styles.menuIcon}
            onClick={toggleMenu}
          />
          {menuAberto && (
            <div style={styles.menuLateral}>
              <button style={styles.menuButton} onClick={() => irPara('/home')}>Início</button>
              <button style={styles.menuButton} onClick={() => irPara('/forum')}>Fórum</button>
              <button style={styles.menuButton} onClick={() => irPara('/calendario')}>Calendário</button>
              <button style={styles.menuButton} onClick={() => irPara('/conteudo')}>Conteúdo</button>
              <button style={styles.menuButton} onClick={sair}>Sair</button>
            </div>
          )}
        </div>
      )}

      {/* Logo Central no Topo */}
      <div style={styles.logoCentralTopo}>
        <img src="/logo.png" alt="Logo AgroPlanner" style={styles.logoTopo} />
      </div>
    </>
  );
}

export default Topo;
