import React from "react";
import Swal from "sweetalert2";

function BotaoAcao({ label, tipo = "padrao", onClick }) {
  const baseClasses =
    "px-4 py-2 rounded-lg text-sm font-medium transition-colors duration-200";

  const estilos = {
    ver: "bg-green-400 text-white hover:bg-green-500",        // verde claro
    aprovar: "bg-blue-500 text-white hover:bg-blue-600",     // azul padrão
    rejeitar: "bg-orange-400 text-white hover:bg-orange-500", // laranja chamativo
    excluir: "bg-red-700 text-white hover:bg-red-800",       // vermelho escuro
    padrao: "bg-gray-300 text-black hover:bg-gray-400",      // neutro
  };

  const handleClick = () => {
    // Executa ação principal
    onClick?.();

    // Evita popup para Voltar ou Ver
    if (label.includes("Voltar") || label.includes("Ver")) return;

    // Mostra popup para os outros botões
    Swal.fire({
      icon: "success",
      title: "Alteração realizada!",
      text: `Ação "${label}" concluída com sucesso.`,
      showConfirmButton: true,
    });
  };

  return (
    <button
      className={`${baseClasses} ${estilos[tipo] || estilos.padrao}`}
      onClick={handleClick}
    >
      {label}
    </button>
  );
}

export default BotaoAcao;
