// Conteudo.jsx atualizado com os novos campos comentados
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/style.css';
import Topo from '../components/Topo';

function Conteudo() {
  const navigate = useNavigate();

  const user = JSON.parse(localStorage.getItem('user'));
  const isAdmin = user?.role === 'admin';
  const idUsuario = user?.id;
  const nomeAutor = user?.nome || 'Anônimo';
  const tipoUsuario = user?.role;

  const [menuAberto, setMenuAberto] = useState(false);
  const [notificacoes, setNotificacoes] = useState([]);
  const [receitas, setReceitas] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [filtro, setFiltro] = useState('');

  const [nomePlanta, setNomePlanta] = useState('');
  const [imagens, setImagens] = useState([]);
  const [categoria, setCategoria] = useState('');
  const [temperatura, setTemperatura] = useState(0);
  const [rega, setRega] = useState(0);
  const [epoca, setEpoca] = useState('');
  const [intervalTempId, setIntervalTempId] = useState(null);
  const [intervalRegaId, setIntervalRegaId] = useState(null);
  const [solo, setSolo] = useState('');
  const [frequenciaUnidade, setFrequenciaUnidade] = useState('');
  const [sol, setSol] = useState('');
  const [instrucoes, setInstrucoes] = useState('');

  // CAMPOS ADICIONAIS COMENTADOS PARA ATIVAR QUANDO QUISER
  const [duracao, setDuracao] = useState(''); // dias de cultivo
  const [distancia, setDistancia] = useState(''); // distância entre plantas
  const [profundidade, setProfundidade] = useState(''); // profundidade de plantio
  const [irrigacao, setIrrigacao] = useState(''); // tipo de irrigação
  const [phMin, setPhMin] = useState(''); // pH mínimo
  const [phMax, setPhMax] = useState(''); // pH máximo
  const [adubacao, setAdubacao] = useState(''); // adubação
  const [espacamento, setEspacamento] = useState(''); // espaçamento entre linhas
  const [altura, setAltura] = useState(''); // altura média da planta

  const [categorias, setCategorias] = useState([]);
  const [epocas, setEpocas] = useState([]);
  const [solos, setSolos] = useState([]);
  const [exposicoes, setExposicoes] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/notificacoes/${idUsuario}`)
      .then(res => {
        const novas = res.data.filter(n => !n.visualizado);
        setNotificacoes(novas);
      })
      .catch(err => console.error('ERRO AO BUSCAR NOTIFICAÇÕES:', err));
  }, []);

  useEffect(() => {
    if (filtro === 'favoritas') {
      axios.get(`http://localhost:5000/api/favoritos/${idUsuario}`)
        .then(res => setReceitas(res.data))
        .catch(err => console.error('ERRO AO BUSCAR FAVORITAS:', err));
    } else {
      axios.get('http://localhost:5000/api/conteudos')
        .then(res => setReceitas(res.data))
        .catch(err => console.error('ERRO AO BUSCAR RECEITAS:', err));
    }
  }, [filtro]);

  useEffect(() => {
    async function carregarOpcoesFormulario() {
      try {
        const [cat, ep, so, sol] = await Promise.all([
          axios.get('http://localhost:5000/api/conteudocategoria'),
          axios.get('http://localhost:5000/api/categoriaepoca'),
          axios.get('http://localhost:5000/api/categoriasolo'),
          axios.get('http://localhost:5000/api/categoriasol')
        ]);
        setCategorias(cat.data);
        setEpocas(ep.data);
        setSolos(so.data);
        setExposicoes(sol.data);
      } catch (error) {
        console.error('Erro ao carregar opções de formulário:', error);
      }
    }
    carregarOpcoesFormulario();
  }, []);

  const startIncrementTemp = () => {
    const id = setInterval(() => {
      setTemperatura(prev => Number(prev) + 1);
    }, 100);
    setIntervalTempId(id);
  };
  const startDecrementTemp = () => {
    const id = setInterval(() => {
      setTemperatura(prev => Math.max(0, Number(prev) - 1));
    }, 100);
    setIntervalTempId(id);
  };
  const stopChangeTemp = () => clearInterval(intervalTempId);

  const startIncrementRega = () => {
    const id = setInterval(() => {
      setRega(prev => Number(prev) + 1);
    }, 100);
    setIntervalRegaId(id);
  };
  const startDecrementRega = () => {
    const id = setInterval(() => {
      setRega(prev => Math.max(0, Number(prev) - 1));
    }, 100);
    setIntervalRegaId(id);
  };
  const stopChangeRega = () => clearInterval(intervalRegaId);

  const deslogar = () => {
    localStorage.clear();
    window.location.href = '/';
  };

  const postarReceita = async (e) => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('nomePlanta', nomePlanta);
      formData.append('categoria', categoria);
      formData.append('epoca', epoca);
      formData.append('temperatura', temperatura);
      formData.append('solo', solo);
      formData.append('rega', `${rega} ${frequenciaUnidade}`);
      formData.append('sol', sol);
      formData.append('instrucoes', instrucoes);
      formData.append('id_autor', idUsuario);
      imagens.forEach(imagem => formData.append('imagens', imagem));

      await axios.post('http://localhost:5000/api/conteudos', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      setNomePlanta('');
      setCategoria('');
      setEpoca('');
      setTemperatura('');
      setSolo('');
      setRega('');
      setFrequenciaUnidade('');
      setSol('');
      setInstrucoes('');
      setImagens([]);
      setMostrarFormulario(false);

      const res = await axios.get('http://localhost:5000/api/conteudos');
      setReceitas(res.data);
    } catch (error) {
      console.error('ERRO AO POSTAR RECEITA:', error);
    }
  };

  const atualizarStatus = async (id, status) => {
    try {
      await axios.put(`http://localhost:5000/api/conteudos/${id}/status`, { status });
      const res = await axios.get('http://localhost:5000/api/conteudos');
      setReceitas(res.data);
    } catch (error) {
      console.error('ERRO AO ATUALIZAR STATUS:', error);
    }
  };

  const excluirReceita = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/conteudos/${id}`);
      const res = await axios.get('http://localhost:5000/api/conteudos');
      setReceitas(res.data);
    } catch (error) {
      console.error('ERRO AO EXCLUIR RECEITA:', error);
    }
  };

  return (
    <div className="conteudo-wrapper">
      <div className="top-bar">
        <Topo centralizado comMenu onMenuClick={() => setMenuAberto(!menuAberto)} />
        {menuAberto && (
          <div className="menu-lateral">
            <button onClick={deslogar}>Sair</button>
          </div>
        )}
      </div>

      {/* continue com o resto do JSX conforme seu formulário atual */}
      {/* ... incluindo os novos campos comentados que posso colar depois se quiser separados */}
    </div>
  );
}

export default Conteudo;
