import React, { useEffect, useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import BotaoAcao from "./BotaoAcao";

function Favoritas() {
  const [favoritos, setFavoritos] = useState([]);
  const user = JSON.parse(localStorage.getItem("user"));
  const idUsuario = user?.id;

  useEffect(() => {
    if (!idUsuario) return;
    carregarFavoritos();
  }, [idUsuario]);

  const carregarFavoritos = () => {
    axios
      .get(`http://localhost:5000/api/favoritos/${idUsuario}`)
      .then((res) => setFavoritos(res.data))
      .catch((err) => console.error("Erro ao carregar favoritos:", err));
  };

  const removerFavorito = async (id_conteudo) => {
    try {
      await axios.put("http://localhost:5000/api/favoritos/remover", {
        id_usuario: idUsuario,
        id_conteudo,
      });
      setFavoritos((prev) => prev.filter((r) => (r.id || r.id_conteudo) !== id_conteudo));
      Swal.fire({
        icon: "success",
        title: "Removido!",
        text: "A receita foi removida dos favoritos.",
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (err) {
      console.error("Erro ao remover favorito:", err);
      Swal.fire({
        icon: "error",
        title: "Erro!",
        text: "Não foi possível remover dos favoritos.",
      });
    }
  };

  // Filtro no padrão
  const favoritosAtivos = (favoritos || []).filter((r) => r.statusAtivo === 1);

  if (favoritosAtivos.length === 0) {
    return (
      <div
        style={{
          backgroundColor: "#fff",
          borderRadius: "12px",
          boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
          padding: "40px 20px",
          textAlign: "center",
          maxWidth: "600px",
          margin: "80px auto",
          fontSize: "1.2rem",
          color: "#777",
        }}
      >
        Você ainda não favoritou nenhuma receita.
      </div>
    );
  }

  return (
    <div
      style={{
        padding: "20px",
        backgroundColor: "#f3f6f9",
        minHeight: "100vh",
        fontFamily: "Arial, sans-serif",
      }}
    >
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
          gap: "20px",
          justifyContent: "center",
          maxWidth: "1200px",
          margin: "0 auto",
        }}
      >
        {favoritosAtivos.map((r) => {
          const id = r.id || r.id_conteudo;
          const imagemSrc = r.imagens
            ? `http://localhost:5000${r.imagens.split(",")[0]}`
            : "/interrogacao.png";

          return (
            <div
              key={id}
              style={{
                backgroundColor: "#fff",
                borderRadius: "12px",
                boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
                overflow: "hidden",
                display: "flex",
                flexDirection: "column",
                minHeight: "400px",
              }}
            >
              {/* Imagem */}
              <div
                style={{
                  width: "100%",
                  height: "180px",
                  overflow: "hidden",
                  backgroundColor: "#2d6a4f",
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  flexShrink: 0,
                  flexDirection: "column",
                  color: "#fff",
                  fontWeight: 600,
                  textAlign: "center",
                }}
              >
                {imagemSrc && imagemSrc !== "/interrogacao.png" ? (
                  <img
                    src={imagemSrc}
                    alt={r.nomePlanta}
                    style={{ width: "100%", height: "100%", objectFit: "cover" }}
                  />
                ) : (
                  <>
                    <img
                      src="/interrogacao.png"
                      alt="Sem imagem"
                      style={{ width: "40px", height: "40px", marginBottom: "10px" }}
                    />
                    <p>Sem Imagem</p>
                  </>
                )}
              </div>

              {/* Conteúdo */}
              <div style={{ padding: "15px", flexGrow: 1, display: "flex", flexDirection: "column" }}>
                <div style={{ fontSize: "0.85rem", color: "#a0a0a0", marginBottom: "5px" }}>ID: {id}</div>
                <h3 style={{ fontSize: "1.1rem", fontWeight: 700, color: "#333", margin: "0 0 10px 0" }}>
                  {r.nomePlanta}
                </h3>
                <p style={{ fontSize: "0.9rem", color: "#555", marginBottom: "15px" }}>
                  <span style={{ fontWeight: 600 }}>Autor:</span> {r.autor || "Anônimo"}
                </p>
              </div>

              {/* Botões */}
              <div
                style={{
                  padding: "0 15px 15px",
                  display: "flex",
                  flexWrap: "wrap",
                  gap: "8px",
                  justifyContent: "center",
                  marginTop: "auto",
                }}
              >
                <BotaoAcao label="Ver" tipo="ver" onClick={() => (window.location.href = `/receita/${id}`)} />
                <BotaoAcao label="Remover" tipo="remover" onClick={() => removerFavorito(id)} />
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Favoritas;
