import React, { useEffect, useState } from 'react';
import axios from 'axios';
import BotaoAcao from './BotaoAcao';

function Favoritas() {
  const [favoritos, setFavoritos] = useState([]);
  const user = JSON.parse(localStorage.getItem('user'));
  const idUsuario = user?.id;

  useEffect(() => {
    if (!idUsuario) return;
    carregarFavoritos();
  }, [idUsuario]);

  const carregarFavoritos = () => {
    axios
      .get(`http://localhost:5000/api/favoritos/${idUsuario}`)
      .then((res) => setFavoritos(res.data))
      .catch((err) => console.error('Erro ao carregar favoritos:', err));
  };

  const removerFavorito = async (id_conteudo) => {
    try {
      await axios.put('http://localhost:5000/api/favoritos/remover', {
        id_usuario: idUsuario,
        id_conteudo,
      });
      carregarFavoritos();
    } catch (err) {
      console.error('Erro ao remover favorito:', err);
    }
  };

  if (favoritos.length === 0) {
    return (
      <div className="flex justify-center mt-8">
        <p className="text-gray-600 text-lg font-medium bg-gray-100 px-6 py-4 rounded-lg shadow-sm">
          Você ainda não favoritou nenhuma receita.
        </p>
      </div>
    );
  }

  return (
    <div
      className="grid gap-6 p-6"
      style={{ gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))' }}
    >
      {favoritos.map((r) => (
        <div
          key={r.id}
          className="bg-white rounded-2xl shadow-md hover:shadow-lg transition-shadow duration-200 flex flex-col"
        >
          {/* Imagem */}
          <div className="relative w-full h-40 rounded-t-2xl overflow-hidden bg-gray-100 flex items-center justify-center">
            <img
              src={
                r.imagens
                  ? `http://localhost:5000${r.imagens.split(',')[0]}`
                  : 'http://localhost:5000/uploads/no-image.png'
              }
              alt={r.nomePlanta}
              className="object-cover w-full h-full"
            />
          </div>

          {/* Conteúdo */}
          <div className="p-4 flex flex-col flex-grow">
            <h3 className="text-lg font-bold text-gray-800">{r.nomePlanta}</h3>
            <p className="text-sm text-gray-600 mt-1">
              <span className="font-semibold">Autor:</span> {r.autor || 'Anônimo'}
            </p>

            {/* Botões */}
            <div className="mt-auto flex gap-3 pt-4">
              <BotaoAcao
                label="Ver receita"
                tipo="ver"
                onClick={() => (window.location.href = `/receita/${r.id}`)}
              />
              <BotaoAcao
                label="Remover"
                tipo="remover"
                onClick={() => removerFavorito(r.id)}
              />
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Favoritas;
