import React from 'react';
import './style.css';

function Topo({ centralizado = false, comMenu = false, comNotificacoes = false, onMenuClick = () => {}, notificacoes = [] }) {
  return (
    <div className={centralizado ? 'top-bar-centralizado' : 'top-bar'}>
      <div className="top-bar-content">
        {comMenu && (
          <img
            src="/menu.png"
            alt="Menu"
            className="menu-icon"
            onClick={onMenuClick}
          />
        )}

        <img
          src="/logo.png"
          alt="Logo AgroPlanner"
          className="logo-topo"
        />

        {comNotificacoes && (
          <div className="notificacao-icone">
            <img
              src={notificacoes.length > 0 ? "/notification-yes.png" : "/notification-no.png"}
              alt="Notificações"
              className="notificacao-img"
            />
            {notificacoes.length > 0 && (
              <span className="notificacao-contador">{notificacoes.length}</span>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default Topo;
