import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import BotaoAcao from './BotaoAcao';

function Enviadas({ receitas, setReceitas }) {
  const navigate = useNavigate();

  const styles = {
    enviadasContainer: {
      padding: '20px',
      backgroundColor: '#f3f6f9',
      minHeight: '100vh',
    },
    receitaListaGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '20px',
      justifyContent: 'center',
      maxWidth: '1200px',
      margin: '0 auto',
    },
    receitaCard: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
      minHeight: '400px',
      padding: '15px',
      textAlign: 'center',
    },
    receitaImagem: {
      width: '100%',
      height: '150px',
      objectFit: 'cover',
      borderRadius: '8px',
      marginBottom: '15px',
    },
    receitaNome: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#333',
      margin: '0 0 10px 0',
    },
    textoAguardando: {
      fontSize: '0.9rem',
      color: '#666',
      marginBottom: '8px',
    },
    textoAprovado: {
      fontSize: '0.9rem',
      color: 'green',
      fontWeight: 'bold',
      marginBottom: '8px',
    },
    mensagemVazia: {
      textAlign: 'center',
      fontSize: '1.2rem',
      color: '#777',
      marginTop: '50px',
    },
    botoesReceita: {
      marginTop: 'auto',
      display: 'flex',
      justifyContent: 'center',
      gap: '8px',
      flexWrap: 'wrap',
    },
    noImageContainer: {
      backgroundColor: '#355c4d',
      borderRadius: '8px',
      width: '100%',
      height: '150px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      marginBottom: '15px',
      color: 'white',
      padding: '10px',
      boxSizing: 'border-box'
    },
    noImageIcon: {
      width: '45px',
      height: '45px',
      marginBottom: '10px',
    },
    noImageText: {
      margin: 0,
      fontSize: '1.1rem',
      fontWeight: 'bold'
    },
  };

  const excluirReceita = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/conteudos/${id}/excluir`);
      setReceitas(prev => prev.filter(r => (r.id || r.id_conteudo) !== id));
      Swal.fire({
        icon: 'success',
        title: 'Excluído!',
        text: 'A receita foi excluída com sucesso.',
        timer: 1500,
        showConfirmButton: false
      });
    } catch (err) {
      console.error('Erro ao excluir receita:', err);
      Swal.fire({
        icon: 'error',
        title: 'Erro!',
        text: 'Não foi possível excluir a receita.',
      });
    }
  };

  const receitasEnviadas = (receitas || []).filter(r => r.statusAtivo === 1);

  return (
    <div style={styles.enviadasContainer}>
      {receitasEnviadas.length === 0 ? (
        <p style={styles.mensagemVazia}>Nenhuma receita encontrada.</p>
      ) : (
        <div style={styles.receitaListaGrid}>
          {receitasEnviadas.map(r => {
            const id = r.id || r.id_conteudo;
            const hasImage = r.imagens?.[0];

            return (
              <div key={id} style={styles.receitaCard}>
                {hasImage ? (
                  <img
                    src={`http://localhost:5000${r.imagens[0]}`}
                    alt={r.nomePlanta}
                    style={styles.receitaImagem}
                  />
                ) : (
                  <div style={styles.noImageContainer}>
                    <img src="/warning_icon.svg" alt="Sem imagem" style={styles.noImageIcon} />
                    <p style={styles.noImageText}>Sem Imagem</p>
                  </div>
                )}
                <h3>{r.nomePlanta}</h3>

                {r.status === 'pendente' && (
                  <>
                    <p style={styles.textoAguardando}>⏳ Aguardando moderação</p>
                    <div style={styles.botoesReceita}>
                      <BotaoAcao
                        label="Excluir"
                        tipo="excluir"
                        onClick={() => excluirReceita(id)}
                      />
                    </div>
                  </>
                )}

                {r.status === 'aprovado' && (
                  <>
                    <p style={styles.textoAprovado}>✅ Aprovado</p>
                    <div style={styles.botoesReceita}>
                      <BotaoAcao
                        label="Ver"
                        tipo="ver"
                        onClick={() => navigate(`/receita/${id}`)}
                      />
                      <BotaoAcao
                        label="Excluir"
                        tipo="excluir"
                        onClick={() => excluirReceita(id)}
                      />
                    </div>
                  </>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default Enviadas;
