import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './style.css';
import Topo from './Topo';

function Home() {
  const [menuAberto, setMenuAberto] = useState(false);
  const [notificacoes, setNotificacoes] = useState([]);
  const navigate = useNavigate();

  // ID fixo para exemplo ‚Äî idealmente isso vem do login
  const idUsuario = 1;

  useEffect(() => {
    axios.get(`http://localhost:5000/api/notificacoes/${idUsuario}`)
      .then(res => {
        const novas = res.data.filter(n => !n.visualizado);
        setNotificacoes(novas);
      })
      .catch(err => console.error('Erro ao buscar notifica√ß√µes:', err));
  }, []);

  const deslogar = () => {
    localStorage.removeItem('usuarioLogado');  // üîí remove flag de login
    window.location.href = '/';                // üîÅ for√ßa redirecionamento
  };


  return (
    <div className="home-container">
      {/* TOPO - Menu + Logo + Notifica√ß√£o */}
      {/* TOPO padronizado */}
      <Topo
        comMenu
        comNotificacoes
        notificacoes={notificacoes}
        onMenuClick={() => setMenuAberto(!menuAberto)}
      />

      {menuAberto && (
        <div className="menu-lateral">
          <button onClick={deslogar}>Sair</button>
        </div>
      )}


      {/* Conte√∫do principal */}
      <div className="home-card">
        <Link to="/forum" className="home-link">
          <img src="/forum.png" alt="F√≥rum" className="card-icon" />
          <h3>F√ìRUM</h3>
          <p>Venha conversar com colegas de plantio.</p>
        </Link>

        <Link to="/calendario" className="home-link">
          <img src="/calendar.png" alt="Calend√°rio" className="card-icon" />
          <h3>Calend√°rio</h3>
          <p>Verifique as datas!!</p>
        </Link>

        <Link to="/conteudo" className="home-link">
          <img src="/content.png" alt="Conte√∫do" className="card-icon" />
          <h3>Conte√∫do</h3>
          <p>Venha ver receitas para voc√™ plantar</p>
        </Link>
      </div>

      <p className="duvida">N√£o encontrou o que gostaria?</p>
      <button className="fale-conosco">Fale conosco</button>
    </div>
  );
}

export default Home;
