import React from "react";
import Swal from "sweetalert2";
import { useNavigate, useLocation } from "react-router-dom";

function BotaoAcao({ label, tipo = "padrao", onClick }) {
  const navigate = useNavigate();
  const location = useLocation();

  const baseStyle = {
    padding: "0.5rem 1rem",
    borderRadius: "0.5rem",
    fontSize: "0.875rem",
    fontWeight: "500",
    transition: "all 0.2s",
    cursor: "pointer",
    border: "none",
  };

  const estilos = {
    ver: { backgroundColor: "#22c55e", color: "white" },        // verde
    aprovar: { backgroundColor: "#3b82f6", color: "white" },    // azul
    rejeitar: { backgroundColor: "#facc15", color: "white" },   // amarelo
    excluir: { backgroundColor: "#dc2626", color: "white" },    // vermelho
    padrao: { backgroundColor: "#6b7280", color: "white" },     // cinza
  };

  const style = { ...baseStyle, ...(estilos[tipo] || estilos.padrao) };

  const handleClick = async () => {
    try {
      if (onClick) await onClick();

      if (tipo === "ver") {
        // ação de visualizar não precisa de pop-up nem redirecionamento
        return;
      }

      // para aprovar, rejeitar ou excluir
      Swal.fire({
        icon: "success",
        title: "Alteração realizada!",
        text: `Ação "${label}" concluída com sucesso.`,
        confirmButtonText: "OK",
      }).then(() => {
        if (location.pathname !== "/conteudo") {
          navigate("/conteudo");
        }
      });
    } catch (err) {
      Swal.fire({
        icon: "error",
        title: "Erro",
        text: "Não foi possível concluir a ação.",
      });
    }
  };

  return (
    <button style={style} onClick={handleClick}>
      {label}
    </button>
  );
}

export default BotaoAcao;
