import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import BotaoAcao from './BotaoAcao';

function Enviadas() {
  const navigate = useNavigate();
  const [receitas, setReceitas] = useState([]);

  // üîπ Carregar receitas enviadas
  useEffect(() => {
    const fetchReceitas = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/conteudos/enviadas');
        setReceitas(res.data || []);
      } catch (err) {
        console.error('Erro ao buscar receitas enviadas:', err);
        Swal.fire({
          icon: 'error',
          title: 'Erro!',
          text: 'N√£o foi poss√≠vel carregar as receitas enviadas.',
        });
      }
    };
    fetchReceitas();
  }, []);

  // üîπ Excluir receita
  const excluirReceita = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/conteudos/${id}/excluir`);
      setReceitas(prev => prev.filter(r => (r.id || r.id_conteudo) !== id));
      Swal.fire({
        icon: 'success',
        title: 'Exclu√≠do!',
        text: 'A receita foi exclu√≠da com sucesso.',
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (err) {
      console.error('Erro ao excluir receita:', err);
      Swal.fire({
        icon: 'error',
        title: 'Erro!',
        text: 'N√£o foi poss√≠vel excluir a receita.',
      });
    }
  };

  const styles = {
    container: {
      padding: '20px',
      backgroundColor: '#f3f6f9',
      minHeight: '100vh',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fit, minmax(280px, 1fr))',
      gap: '20px',
      justifyContent: 'center',
      maxWidth: '1200px',
      margin: '0 auto',
    },
    card: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
      minHeight: '400px',
      padding: '15px',
      textAlign: 'center',
    },
    imagem: {
      width: '100%',
      height: '150px',
      objectFit: 'cover',
      borderRadius: '8px',
      marginBottom: '15px',
    },
    nome: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#333',
      margin: '0 0 10px 0',
    },
    aguardando: {
      fontSize: '0.9rem',
      color: '#666',
      marginBottom: '8px',
    },
    aprovado: {
      fontSize: '0.9rem',
      color: 'green',
      fontWeight: 'bold',
      marginBottom: '8px',
    },
    vazio: {
      textAlign: 'center',
      fontSize: '1.2rem',
      color: '#777',
      marginTop: '50px',
    },
    botoes: {
      marginTop: 'auto',
      display: 'flex',
      justifyContent: 'center',
      gap: '8px',
      flexWrap: 'wrap',
    },
    noImageContainer: {
      backgroundColor: '#355c4d',
      borderRadius: '8px',
      width: '100%',
      height: '150px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      marginBottom: '15px',
      color: 'white',
      padding: '10px',
      boxSizing: 'border-box',
    },
    noImageIcon: {
      width: '45px',
      height: '45px',
      marginBottom: '10px',
    },
    noImageText: {
      margin: 0,
      fontSize: '1.1rem',
      fontWeight: 'bold',
    },
  };

  return (
    <div style={styles.container}>
      {receitas.length === 0 ? (
        <p style={styles.vazio}>Nenhuma receita encontrada.</p>
      ) : (
        <div style={styles.grid}>
          {receitas.map(r => {
            const id = r.id || r.id_conteudo;
            const hasImage = r.imagens?.[0];

            return (
              <div key={id} style={styles.card}>
                {hasImage ? (
                  <img
                    src={`http://localhost:5000${r.imagens[0]}`}
                    alt={r.nomePlanta}
                    style={styles.imagem}
                  />
                ) : (
                  <div style={styles.noImageContainer}>
                    <img src="/warning_icon.svg" alt="Sem imagem" style={styles.noImageIcon} />
                    <p style={styles.noImageText}>Sem Imagem</p>
                  </div>
                )}

                <h3 style={styles.nome}>{r.nomePlanta}</h3>

                {r.status === 'pendente' && (
                  <>
                    <p style={styles.aguardando}>‚è≥ Aguardando modera√ß√£o</p>
                    <div style={styles.botoes}>
                      <BotaoAcao
                        label="Excluir"
                        tipo="excluir"
                        onClick={() => excluirReceita(id)}
                      />
                    </div>
                  </>
                )}

                {r.status === 'aprovado' && (
                  <>
                    <p style={styles.aprovado}>‚úÖ Aprovado</p>
                    <div style={styles.botoes}>
                      <BotaoAcao
                        label="Ver"
                        tipo="ver"
                        onClick={() => navigate(`/receita/${id}`)}
                      />
                      <BotaoAcao
                        label="Excluir"
                        tipo="excluir"
                        onClick={() => excluirReceita(id)}
                      />
                    </div>
                  </>
                )}
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default Enviadas;
