import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import Topo from '../components/Topo';

// √çcones simples
const IconePerfil = () => <span style={{ fontSize: '1.2em' }}>üë§</span>;
const IconeDocumento = () => <span style={{ fontSize: '1.2em' }}>üìÑ</span>;
const IconeConfig = () => <span style={{ fontSize: '1.2em' }}>‚öôÔ∏è</span>;
const IconeLogin = () => <span style={{ fontSize: '1.2em' }}>üîë</span>;
const IconeFavoritos = () => <span style={{ fontSize: '1.5rem', color: '#ff6961' }}>‚ù§</span>;
const IconePostagens = () => <span style={{ fontSize: '1.5rem', color: '#2c7be5' }}>‚úçÔ∏è</span>;

function Perfil() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [cargo, setCargo] = useState('');
  const [status, setStatus] = useState('');
  const [editando, setEditando] = useState(false);
  const [mensagem, setMensagem] = useState('');
  const [totalFavoritos, setTotalFavoritos] = useState(0);
  const [totalPostagens, setTotalPostagens] = useState(0);
  const [erroApi, setErroApi] = useState('');
  const [carregando, setCarregando] = useState(false);

  // foto de perfil
  const [fotoPerfil, setFotoPerfil] = useState('');        // caminho salvo no banco
  const [arquivoFoto, setArquivoFoto] = useState(null);    // File selecionado
  const [previewFoto, setPreviewFoto] = useState('');      // preview local

  const navigate = useNavigate();
  const API_BASE = useMemo(() => process.env.REACT_APP_API_BASE || 'http://localhost:5000', []);

  const atividadesRecentes = [
    { id: 1, texto: 'Perfil atualizado', tipo: 'profile', icone: <IconePerfil /> },
    { id: 2, texto: 'Documento enviado', tipo: 'document', icone: <IconeDocumento /> },
    { id: 3, texto: 'Configura√ß√µes alteradas', tipo: 'settings', icone: <IconeConfig /> },
    { id: 4, texto: 'Login realizado', tipo: 'login', icone: <IconeLogin /> },
  ];

  // helper para montar a URL da imagem (aceita absoluta ou /uploads/‚Ä¶)
  const resolveImgSrc = (valor) => {
    if (!valor) return `${API_BASE}/uploads/no-image.png`;
    // se j√° √© absoluta (http/https), usa direto
    if (/^https?:\/\//i.test(valor)) return valor;
    // caso contr√°rio, prefixa pelo backend
    return `${API_BASE}${valor}`;
  };

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user || !user.id) {
      navigate('/');
      return;
    }
    const idUsuario = user.id;

    setNome(user.nome || 'Nome do Usu√°rio');
    setEmail(user.email || 'email@exemplo.com');
    setCargo(user.role || user.tipo || 'Usu√°rio Padr√£o');
    setStatus(user.status || 'Ativo');
    setFotoPerfil(user.foto_perfil || '');    // pode vir vazio no in√≠cio
    setPreviewFoto('');                       // limpa o preview

    const buscarDadosDoBackend = async () => {
      setErroApi('');
      setCarregando(true);
      const ctrl = new AbortController();

      try {
        const [favoritosRes, postagensRes] = await Promise.all([
          fetch(`${API_BASE}/api/favoritos/contagem/${idUsuario}`, { signal: ctrl.signal }),
          fetch(`${API_BASE}/api/conteudos/contagem/${idUsuario}`, { signal: ctrl.signal }),
        ]);

        if (!favoritosRes.ok) {
          const txt = await favoritosRes.text();
          throw new Error(`Erro favoritos (${favoritosRes.status}): ${txt}`);
        }
        if (!postagensRes.ok) {
          const txt = await postagensRes.text();
          throw new Error(`Erro postagens (${postagensRes.status}): ${txt}`);
        }

        const favoritosData = await favoritosRes.json();
        const postagensData = await postagensRes.json();

        setTotalFavoritos(Number(favoritosData?.totalFavoritos) || 0);
        setTotalPostagens(Number(postagensData?.totalPostagens) || 0);
      } catch (error) {
        console.error('Erro ao buscar dados:', error);
        setErroApi('N√£o foi poss√≠vel conectar ao servidor. Por favor, tente novamente mais tarde.');
      } finally {
        setCarregando(false);
      }

      return () => ctrl.abort();
    };

    buscarDadosDoBackend();
  }, [navigate, API_BASE]);

  // quando o usu√°rio escolhe uma imagem, prepara preview
  const onChangeArquivo = (e) => {
    const file = e.target.files?.[0];
    if (!file) return;
    setArquivoFoto(file);
    const url = URL.createObjectURL(file);
    setPreviewFoto(url);
  };

  // salva campos b√°sicos no localStorage (como voc√™ j√° fazia)
  const handleSalvarTextoLocal = () => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      const usuarioAtualizado = {
        ...user,
        nome,
        email,
        role: cargo,
        tipo: cargo,      // mant√©m compat√≠vel
        status,
        foto_perfil: fotoPerfil // mantemos o que j√° est√° salvo (ou ser√° atualizado ap√≥s upload)
      };
      localStorage.setItem('user', JSON.stringify(usuarioAtualizado));
    }
  };

  // submit principal
  const handleSalvar = async (e) => {
    e.preventDefault();
    setMensagem('');

    const user = JSON.parse(localStorage.getItem('user'));
    if (!user?.id) return;

    try {
      // 1) atualiza campos de texto no backend
      const respTexto = await fetch(`${API_BASE}/api/users/${user.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          nome,
          email,
          tipo: cargo,
          // se quiser, envie tamb√©m os campos de endere√ßo/telefone
          // endereco_num, endereco_rua, endereco_bairro, endereco_cep, numero_celular
        }),
      });

      if (!respTexto.ok) {
        const txt = await respTexto.text();
        throw new Error(`Erro ao atualizar dados: ${txt}`);
      }

      // 2) se o usu√°rio selecionou uma nova foto, sobe a foto
      if (arquivoFoto) {
        const formData = new FormData();
        formData.append('foto', arquivoFoto);

        const respFoto = await fetch(`${API_BASE}/api/users/${user.id}/foto`, {
          method: 'PUT',
          body: formData,
        });

        if (!respFoto.ok) {
          const txt = await respFoto.text();
          throw new Error(`Erro ao enviar foto: ${txt}`);
        }

        const dataFoto = await respFoto.json();
        // atualiza estados/LS com o caminho salvo (/uploads/arquivo)
        setFotoPerfil(dataFoto.foto_perfil || '');
        const atual = JSON.parse(localStorage.getItem('user')) || {};
        localStorage.setItem('user', JSON.stringify({ ...atual, foto_perfil: dataFoto.foto_perfil || '' }));
      }

      // 3) persist√™ncia local dos campos de texto
      handleSalvarTextoLocal();

      setEditando(false);
      setMensagem('Perfil atualizado com sucesso!');
      setTimeout(() => setMensagem(''), 3000);
      setArquivoFoto(null);
      setPreviewFoto('');
    } catch (err) {
      console.error(err);
      setErroApi('Erro ao salvar perfil. Tente novamente.');
      setTimeout(() => setErroApi(''), 4000);
    }
  };

  const deslogar = () => {
    localStorage.clear();
    navigate('/');
  };

  const Tag = ({ text, color, bgColor }) => (
    <span
      style={{
        padding: '4px 8px',
        borderRadius: '5px',
        backgroundColor: bgColor || '#e0e0e0',
        color: color || '#333',
        fontSize: '0.8em',
        fontWeight: 'bold',
        marginRight: '5px',
      }}
    >
      {text}
    </span>
  );

  // imagem exibida (usa preview se estiver editando e j√° escolheu arquivo)
  const avatarSrc = previewFoto || resolveImgSrc(fotoPerfil);

  return (
    <div style={styles.pagina}>
      <Topo />
      <div style={styles.mainContent}>
        <div style={styles.leftColumn}>
          <div style={styles.profileCard}>
            <img src={avatarSrc} alt="Avatar do Usu√°rio" style={styles.avatar} />

            {editando && (
              <div style={{ marginBottom: 12, textAlign: 'center' }}>
                <input
                  type="file"
                  accept="image/*"
                  onChange={onChangeArquivo}
                  style={{ display: 'block' }}
                />
                <small>Formatos comuns (jpg, png). A imagem ser√° salva no servidor.</small>
              </div>
            )}

            {editando ? (
              <input
                type="text"
                value={nome}
                onChange={(e) => setNome(e.target.value)}
                style={styles.nomeInput}
              />
            ) : (
              <h2 style={styles.userName}>{nome}</h2>
            )}

            <div style={styles.tagsContainer}>
              {editando ? (
                <select value={cargo} onChange={(e) => setCargo(e.target.value)} style={styles.selectInput}>
                  <option value="admin">admin</option>
                  <option value="comum">comum</option>
                  <option value="Usu√°rio Padr√£o">Usu√°rio Padr√£o</option>
                </select>
              ) : (
                <Tag text={cargo} bgColor="#e7f0ff" color="#2c7be5" />
              )}
              {editando ? (
                <select value={status} onChange={(e) => setStatus(e.target.value)} style={styles.selectInput}>
                  <option value="Ativo">Ativo</option>
                  <option value="Inativo">Inativo</option>
                </select>
              ) : (
                <Tag text={status} bgColor="#d4edda" color="#28a745" />
              )}
            </div>

            {editando && (
              <div style={styles.emailEdit}>
                <label style={styles.labelInput}>Email:</label>
                <input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  style={styles.inputField}
                />
              </div>
            )}

            <div style={styles.contactIcons}>
              <span style={styles.iconItem}>‚úâÔ∏è</span>
              <span style={styles.iconItem}>üìû</span>
              <span style={styles.iconItem}>üìç</span>
            </div>

            <div style={styles.profileButtons}>
              {editando ? (
                <button onClick={handleSalvar} style={styles.editButton}>
                  Salvar Perfil
                </button>
              ) : (
                <button onClick={() => setEditando(true)} style={styles.editButton}>
                  ‚úèÔ∏è Editar Perfil
                </button>
              )}
              <button style={styles.settingsButton}>‚öôÔ∏è</button>
            </div>
            <button onClick={deslogar} style={styles.logoutButton}>
              Sair
            </button>

            {mensagem && <p style={styles.mensagemSucesso}>{mensagem}</p>}
            {erroApi && <p style={styles.mensagemErro}>{erroApi}</p>}
          </div>
        </div>

        <div style={styles.rightColumn}>
          <div style={styles.statsGrid}>
            <div style={styles.statCard}>
              <div style={styles.statInfo}>
                <span style={styles.statNumber}>
                  {carregando ? '...' : totalFavoritos}
                </span>
                <span style={styles.statLabel}>Postagens favoritadas</span>
              </div>
              <IconeFavoritos />
            </div>

            <div style={styles.statCard}>
              <div style={styles.statInfo}>
                <span style={styles.statNumber}>
                  {carregando ? '...' : totalPostagens}
                </span>
                <span style={styles.statLabel}>Postagens feitas</span>
              </div>
              <IconePostagens />
            </div>
          </div>

          <div style={styles.activitiesCard}>
            <h3 style={styles.activitiesTitle}>Atividades Recentes</h3>
            {atividadesRecentes.map((activity, index) => (
              <div
                key={activity.id}
                style={{
                  ...styles.activityItem,
                  borderBottom: index === atividadesRecentes.length - 1 ? 'none' : '1px solid #eee',
                }}
              >
                <div style={styles.activityIcon}>{activity.icone}</div>
                <span style={styles.activityText}>{activity.texto}</span>
                <Tag
                  text={activity.tipo}
                  bgColor={
                    activity.tipo === 'profile'
                      ? '#ffe0b2'
                      : activity.tipo === 'document'
                      ? '#c8e6c9'
                      : activity.tipo === 'settings'
                      ? '#e1bee7'
                      : activity.tipo === 'login'
                      ? '#bbdefb'
                      : '#e0e0e0'
                  }
                  color={
                    activity.tipo === 'profile'
                      ? '#e65100'
                      : activity.tipo === 'document'
                      ? '#2e7d32'
                      : activity.tipo === 'settings'
                      ? '#6a1b9a'
                      : activity.tipo === 'login'
                      ? '#1565c0'
                      : '#333'
                  }
                />
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

