import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Topo from './Topo';
import './style.css';

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const idUsuario = user?.id;

  const [receita, setReceita] = useState(null);
  const [favorito, setFavorito] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => setReceita(res.data))
      .catch(err => console.error('Erro ao buscar receita:', err));

    axios.get(`http://localhost:5000/api/favoritos/${idUsuario}`)
      .then(res => {
        const favoritos = res.data.map(f => f.id_conteudo);
        setFavorito(favoritos.includes(Number(id)));
      })
      .catch(err => console.error('Erro ao verificar favorito:', err));
  }, [id, idUsuario]);

  const toggleFavorito = () => {
    const url = `http://localhost:5000/api/favoritos/${idUsuario}/${id}`;
    if (favorito) {
      axios.delete(url).then(() => setFavorito(false));
    } else {
      axios.post(url).then(() => setFavorito(true));
    }
  };

  if (!receita) return <p>Carregando receita...</p>;

  return (
    <div className="receita-detalhe">
      <Topo comMenu />

      <div className="conteudo-detalhe">
        {receita.imagens?.length > 0 ? (
          <img src={`http://localhost:5000${receita.imagens[0]}`} alt={receita.nomePlanta} />
        ) : (
          <img src="/no-image.png" alt="Sem imagem" />
        )}

        <h1>{receita.nomePlanta}</h1>
        <p><strong>Categoria:</strong> {receita.categoria}</p>
        <p><strong>Plantio:</strong> {receita.epoca}</p>
        <p><strong>Temperatura:</strong> {receita.temperatura}°C</p>
        <p><strong>Solo:</strong> {receita.solo}</p>
        <p><strong>Rega:</strong> {receita.rega}</p>
        <p><strong>Sol:</strong> {receita.sol}</p>
        <p><strong>Instruções:</strong> {receita.instrucoes}</p>

        {/* Botão estrela */}
        <div style={{ display: 'flex', justifyContent: 'end', marginTop: '1rem' }}>
          <img
            src={favorito ? "/star.png" : "/no-star.png"}
            alt="Favorito"
            style={{ width: '32px', height: '32px', cursor: 'pointer' }}
            onClick={toggleFavorito}
          />
        </div>

        <button onClick={() => navigate(-1)} className="botao-voltar">Voltar</button>
      </div>
    </div>
  );
}

export default ReceitaDetalhe;
