// Filtra receitas enviadas pelo usuário que estão ativas e não rejeitadas
export function filtrarReceitasEnviadas(receitas, idUsuario) {
  return receitas.filter(r =>
    r.id_autor === idUsuario &&
    r.statusAtivo !== 0 &&
    r.status !== 'rejeitado'
  );
}

// Ordena por data de publicação
export function ordenarPorData(lista, ordem = 'desc') {
  return [...lista].sort((a, b) => {
    const dataA = new Date(a.fecha_publicacion);
    const dataB = new Date(b.fecha_publicacion);
    return ordem === 'asc' ? dataA - dataB : dataB - dataA;
  });
}
