import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import Topo from '../components/Topo';
import BotaoAcao from '../components/BotaoAcao';
import '../styles/style.css';

function ReceitaDetalhe() {
Â  const { id } = useParams();
Â  const navigate = useNavigate();
Â  const [receita, setReceita] = useState(null);
Â  const [favoritado, setFavoritado] = useState(false);

Â  const user = JSON.parse(localStorage.getItem('user'));
Â  const idUsuario = user?.id;
Â  const isModerador = user?.role === 'admin';

Â  useEffect(() => {
Â  Â  axios.get(`http://localhost:5000/api/conteudos/${id}`)
Â  Â  Â  .then(res => setReceita(res.data))
Â  Â  Â  .catch(err => console.error('Erro ao buscar receita:', err));
Â  }, [id]);

Â  useEffect(() => {
Â  Â  if (idUsuario) {
Â  Â  Â  axios.get(`http://localhost:5000/api/favoritos/${idUsuario}/${id}`)
Â  Â  Â  Â  .then(res => {
Â  Â  Â  Â  Â  const favorito = res.data;
Â  Â  Â  Â  Â  setFavoritado(favorito?.statusAtivo === 1);
Â  Â  Â  Â  })
Â  Â  Â  Â  .catch(err => console.error('Erro ao verificar favorito:', err));
Â  Â  }
Â  }, [id, idUsuario]);

Â  const toggleFavorito = async () => {
Â  Â  try {
Â  Â  Â  const response = await axios.get(`http://localhost:5000/api/favoritos/${idUsuario}/${id}`);
Â  Â  Â  const favorito = response.data;

Â  Â  Â  if (!favorito) {
Â  Â  Â  Â  await axios.post(`http://localhost:5000/api/favoritos`, {
Â  Â  Â  Â  Â  id_usuario: idUsuario,
Â  Â  Â  Â  Â  id_conteudo: id,
Â  Â  Â  Â  Â  statusAtivo: 1
Â  Â  Â  Â  });
Â  Â  Â  Â  setFavoritado(true);
Â  Â  Â  } else {
Â  Â  Â  Â  if (favorito.statusAtivo === 1) {
Â  Â  Â  Â  Â  await axios.put(`http://localhost:5000/api/favoritos/remover`, {
Â  Â  Â  Â  Â  Â  id_usuario: idUsuario,
Â  Â  Â  Â  Â  Â  id_conteudo: id
Â  Â  Â  Â  Â  });
Â  Â  Â  Â  Â  setFavoritado(false);
Â  Â  Â  Â  } else {
Â  Â  Â  Â  Â  await axios.post(`http://localhost:5000/api/favoritos`, {
Â  Â  Â  Â  Â  Â  id_usuario: idUsuario,
Â  Â  Â  Â  Â  Â  id_conteudo: id,
Â  Â  Â  Â  Â  Â  statusAtivo: 1
Â  Â  Â  Â  Â  });
Â  Â  Â  Â  Â  setFavoritado(true);
Â  Â  Â  Â  }
Â  Â  Â  }
Â  Â  } catch (err) {
Â  Â  Â  console.error('Erro ao atualizar favorito:', err);
Â  Â  }
Â  };

Â  const atualizarStatus = async (status) => {
Â  Â  try {
Â  Â  Â  await axios.put(`http://localhost:5000/api/conteudos/${id}/status`, { status });

Â  Â  Â  Swal.fire({
Â  Â  Â  Â  icon: 'success',
Â  Â  Â  Â  title: `Receita ${status === 'aprovado' ? 'aprovada' : 'rejeitada'} com sucesso!`,
Â  Â  Â  Â  showConfirmButton: false,
Â  Â  Â  Â  timer: 1500
Â  Â  Â  });

Â  Â  Â  setTimeout(() => {
Â  Â  Â  Â  navigate('/conteudo');
Â  Â  Â  }, 1600);
Â  Â  } catch (err) {
Â  Â  Â  console.error('Erro ao atualizar status:', err);
Â  Â  Â  Swal.fire({
Â  Â  Â  Â  icon: 'error',
Â  Â  Â  Â  title: 'Erro ao atualizar status',
Â  Â  Â  Â  text: 'Tente novamente mais tarde.'
Â  Â  Â  });
Â  Â  }
Â  };

Â  if (!receita) return <div>Carregando receita...</div>;

Â  const dataFormatada = new Date(receita.data_publicacao).toLocaleDateString('pt-BR');

Â  // ğŸ”” AlteraÃ§Ã£o: Preparando as instruÃ§Ãµes para quebra de linha
Â  const instrucoesComQuebra = receita.instrucoes.replace(/\n/g, '<br />');

Â  return (
Â  Â  <div style={{
Â  Â  Â  backgroundColor: '#f5f5f5',
Â  Â  Â  minHeight: '100vh',
Â  Â  Â  fontFamily: 'Arial, sans-serif'
Â  Â  }}>
Â  Â  Â  {/* ğŸ” Topo fixo */}
Â  Â  Â  <div style={{
Â  Â  Â  Â  backgroundColor: '#fff',
Â  Â  Â  Â  padding: '10px 20px',
Â  Â  Â  Â  boxShadow: '0 2px 5px rgba(0,0,0,0.1)',
Â  Â  Â  Â  display: 'flex',
Â  Â  Â  Â  justifyContent: 'space-between',
Â  Â  Â  Â  alignItems: 'center',
Â  Â  Â  Â  position: 'sticky',
Â  Â  Â  Â  top: 0,
Â  Â  Â  Â  zIndex: 1000
Â  Â  Â  }}>
Â  Â  Â  Â  <Topo centralizado comMenu />
Â  Â  Â  </div>

Â  Â  Â  {/* ğŸ“Œ Card centralizado */}
Â  Â  Â  <div style={{
Â  Â  Â  Â  display: 'flex',
Â  Â  Â  Â  justifyContent: 'center',
Â  Â  Â  Â  alignItems: 'flex-start',
Â  Â  Â  Â  padding: '40px 20px'
Â  Â  Â  }}>
Â  Â  Â  Â  <div style={{
Â  Â  Â  Â  Â  width: '100%',
Â  Â  Â  Â  Â  maxWidth: '800px',
Â  Â  Â  Â  Â  backgroundColor: '#fff',
Â  Â  Â  Â  Â  borderRadius: '16px',
Â  Â  Â  Â  Â  boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
Â  Â  Â  Â  Â  overflow: 'hidden',
Â  Â  Â  Â  Â  fontFamily: 'sans-serif'
Â  Â  Â  Â  }}>
Â  Â  Â  Â  Â  {/* Top Header Section */}
Â  Â  Â  Â  Â  <div style={{
Â  Â  Â  Â  Â  Â  position: 'relative',
Â  Â  Â  Â  Â  Â  backgroundColor: '#2d6a4f',
Â  Â  Â  Â  Â  Â  padding: '20px',
Â  Â  Â  Â  Â  Â  color: '#fff',
Â  Â  Â  Â  Â  Â  display: 'flex',
Â  Â  Â  Â  Â  Â  flexDirection: 'column',
Â  Â  Â  Â  Â  Â  alignItems: 'flex-start',
Â  Â  Â  Â  Â  Â  borderRadius: '16px 16px 0 0'
Â  Â  Â  Â  Â  }}>
Â  Â  Â  Â  Â  Â  {/* Favorite Button */}
Â  Â  Â  Â  Â  Â  <div
Â  Â  Â  Â  Â  Â  Â  onClick={toggleFavorito}
Â  Â  Â  Â  Â  Â  Â  title={favoritado ? 'Remover dos favoritos' : 'Adicionar aos favoritos'}
Â  Â  Â  Â  Â  Â  Â  style={{
Â  Â  Â  Â  Â  Â  Â  Â  position: 'absolute',
Â  Â  Â  Â  Â  Â  Â  Â  top: '15px',
Â  Â  Â  Â  Â  Â  Â  Â  right: '20px',
Â  Â  Â  Â  Â  Â  Â  Â  cursor: 'pointer',
Â  Â  Â  Â  Â  Â  Â  Â  zIndex: 10
Â  Â  Â  Â  Â  Â  Â  }}
Â  Â  Â  Â  Â  Â  >
Â  Â  Â  Â  Â  Â  Â  <img
Â  Â  Â  Â  Â  Â  Â  Â  src={favoritado ? '/heart.png' : '/broken-heart.png'}
Â  Â  Â  Â  Â  Â  Â  Â  alt="Favorito"
Â  Â  Â  Â  Â  Â  Â  Â  width="40"
Â  Â  Â  Â  Â  Â  Â  />
Â  Â  Â  Â  Â  Â  </div>

Â  Â  Â  Â  Â  Â  <div style={{
Â  Â  Â  Â  Â  Â  Â  display: 'flex',
Â  Â  Â  Â  Â  Â  Â  alignItems: 'center',
Â  Â  Â  Â  Â  Â  Â  width: '100%',
Â  Â  Â  Â  Â  Â  Â  gap: '20px'
Â  Â  Â  Â  Â  Â  }}>
Â  Â  Â  Â  Â  Â  Â  {/* Plant Image */}
Â  Â  Â  Â  Â  Â  Â  <div style={{
Â  Â  Â  Â  Â  Â  Â  Â  width: '120px',
Â  Â  Â  Â  Â  Â  Â  Â  height: '120px',
Â  Â  Â  Â  Â  Â  Â  Â  borderRadius: '8px',
Â  Â  Â  Â  Â  Â  Â  Â  overflow: 'hidden',
Â  Â  Â  Â  Â  Â  Â  Â  flexShrink: 0,
Â  Â  Â  Â  Â  Â  Â  Â  display: 'flex',
Â  Â  Â  Â  Â  Â  Â  Â  alignItems: 'center',
Â  Â  Â  Â  Â  Â  Â  Â  justifyContent: 'center',
Â  Â  Â  Â  Â  Â  Â  Â  backgroundColor: '#2d6a4f',
Â  Â  Â  Â  Â  Â  Â  Â  color: '#fff',
Â  Â  Â  Â  Â  Â  Â  Â  fontWeight: '600',
Â  Â  Â  Â  Â  Â  Â  Â  textAlign: 'center'
Â  Â  Â  Â  Â  Â  Â  }}>
Â  Â  Â  Â  Â  Â  Â  Â  {receita.imagens?.[0] ? (
Â  Â  Â  Â  Â  Â  Â  Â  Â  <img
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  src={`http://localhost:5000${receita.imagens[0]}`}
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  alt={receita.nomePlanta}
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  style={{ width: '100%', height: '100%', objectFit: 'cover' }}
Â  Â  Â  Â  Â  Â  Â  Â  Â  />
Â  Â  Â  Â  Â  Â  Â  Â  ) : (
Â  Â  Â  Â  Â  Â  Â  Â  Â  <>
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <img src="/interrogacao.png" alt="Sem imagem" style={{ width: '40px', height: '40px', marginBottom: '8px' }} />
Â  Â  Â  Â  Â  Â  Â  Â  Â  Â  <p style={{ fontSize: '12px' }}>Sem Imagem</p>
Â  Â  Â  Â  Â  Â  Â  Â  Â  </>
Â  Â  Â  Â  Â  Â  Â  Â  )}
Â  Â  Â  Â  Â  Â  Â  </div>

Â  Â  Â  Â  Â  Â  Â  {/* Title and Info */}
Â  Â  Â  Â  Â  Â  Â  <div style={{ display: 'flex', flexDirection: 'column', gap: '8px', flexGrow: 1 }}>
Â  Â  Â  Â  Â  Â  Â  Â  <div style={{ fontSize: '24px', margin: 0, fontWeight: 'bold' }}>{receita.nomePlanta}</div>

Â  Â  Â  Â  Â  Â  Â  Â  {/* Autor */}
