import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import BotaoAcao from './BotaoAcao';
import { filtrarReceitasEnviadas, ordenarPorData } from './filtrosReceita';

function Enviadas({ receitas, idUsuario }) {
  const navigate = useNavigate();
  const [ordemData, setOrdemData] = useState('desc'); // 'desc' = mais novas primeiro

  const excluirReceita = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/conteudos/${id}/excluir`);
      window.location.reload();
    } catch (err) {
      console.error('Erro ao excluir receita:', err);
    }
  };

  const receitasFiltradas = filtrarReceitasEnviadas(receitas, idUsuario);
  const receitasOrdenadas = ordenarPorData(receitasFiltradas, ordemData);

  return (
    <div className="receita-lista">
      <div className="filtro-data">
        <label htmlFor="ordem">Ordenar por data:</label>
        <select
          id="ordem"
          value={ordemData}
          onChange={e => setOrdemData(e.target.value)}
        >
          <option value="desc">Mais novas primeiro</option>
          <option value="asc">Mais velhas primeiro</option>
        </select>
      </div>

      {receitasOrdenadas.length === 0 ? (
        <p className="mensagem-vazia">Você ainda não enviou nenhuma receita.</p>
      ) : (
        receitasOrdenadas.map(r => (
          <div key={r.id || r.id_conteudo} className="receita-card simples">
            <img
              src={`http://localhost:5000${r.imagens?.[0] || '/uploads/no-image.png'}`}
              alt={r.nomePlanta}
              className="receita-imagem"
              onError={e => e.target.src = 'http://localhost:5000/uploads/no-image.png'}
            />
            <h3>{r.nomePlanta}</h3>

            {r.status === 'pendente' && (
              <>
                <p className="aguardando">⏳ Aguardando moderação</p>
                <div className="botoes-receita">
                  <BotaoAcao
                    label="Excluir"
                    tipo="excluir"
                    onClick={() => excluirReceita(r.id || r.id_conteudo)}
                  />
                </div>
              </>
            )}

            {r.status === 'aprovado' && (
              <>
                <p className="texto-aprovado">✅ Aprovado</p>
                <div className="botoes-receita flex gap-2">
                  <BotaoAcao
                    label="Ver"
                    tipo="ver"
                    onClick={() => navigate(`/receita/${r.id || r.id_conteudo}`)}
                  />
                  <BotaoAcao
                    label="Excluir"
                    tipo="excluir"
                    onClick={() => excluirReceita(r.id || r.id_conteudo)}
                  />
                </div>
              </>
            )}
          </div>
        ))
      )}
    </div>
  );
}

export default Enviadas;
