import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Favoritas({ idUsuario }) {
  const [favoritos, setFavoritos] = useState([]);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/favoritos/${idUsuario}`)
      .then(res => setFavoritos(res.data))
      .catch(err => console.error('Erro ao carregar favoritos:', err));
  }, [idUsuario]);

  if (favoritos.length === 0) {
    return <p className="mensagem-vazia">Você ainda não favoritou nenhuma receita.</p>;
  }

  return (
    <div className="receita-lista">
      {favoritos.map(r => (
        <div key={r.id} className="receita-card simples">
          <img src={`http://localhost:5000${r.imagens?.[0] || '/uploads/no-image.png'}`} alt={r.nomePlanta} />
          <h3>{r.nomePlanta}</h3>
          <p><strong>Autor:</strong> {r.autor || 'Anônimo'}</p>
          <button className="btn-ver" onClick={() => window.location.href = `/receita/${r.id}`}>Ver receita</button>
        </div>
      ))}
    </div>
  );
}

export default Favoritas;
