import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BotaoAcao from './BotaoAcao';
import Filtro from './FiltroEnviadas';

function Publicadas({ receitas }) {
  const navigate = useNavigate();

  const [filtros, setFiltros] = useState({
    id_categoria: '',
    id_epoca: '',
    id_solo: '',
    id_sol: ''
  });

  const aplicarFiltros = (f) => setFiltros(f);

  // Filtra só aprovadas
  const receitasAprovadas = (receitas || []).filter(r => r.status === 'aprovado');

  // Aplica filtros escolhidos
  const receitasFiltradas = receitasAprovadas.filter(r => {
    const { id_categoria, id_epoca, id_solo, id_sol } = filtros;

    return (
      (id_categoria ? r.id_categoria == id_categoria : true) &&
      (id_epoca ? r.id_epoca == id_epoca : true) &&
      (id_solo ? r.id_solo == id_solo : true) &&
      (id_sol ? r.id_sol == id_sol : true)
    );
  });

  const styles = {
    container: {
      display: 'flex',
      gap: '20px',
      padding: '20px',
      minHeight: '100vh',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#f0f4f7',
      width: '90%',
      margin: '0 auto',
    },
    filtrosCol: {
      width: '280px',
      backgroundColor: '#fff',
      borderRadius: '12px',
      padding: '20px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.08)',
      height: 'fit-content',
      position: 'sticky',
      top: '20px',
      flexShrink: 0,
    },
    conteudo: {
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
    },
    grid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(auto-fill, minmax(280px, 1fr))',
      gap: '20px',
      maxWidth: '1200px',
      margin: '0 auto',
    },
    card: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)',
      display: 'flex',
      flexDirection: 'column',
      overflow: 'hidden',
    },
    imagemContainer: {
      width: '100%',
      height: '180px',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      backgroundColor: '#2d6a4f',
      color: '#fff',
      fontWeight: '600',
      flexDirection: 'column',
    },
    imagem: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
    },
    info: {
      padding: '15px',
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
    },
    nome: {
      fontSize: '1.1rem',
      fontWeight: '700',
      color: '#333',
      marginBottom: '10px',
    },
    meta: {
      fontSize: '0.9rem',
      color: '#666',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      marginBottom: '8px',
    },
    botoes: {
      padding: '15px',
      display: 'flex',
      justifyContent: 'center',
    },
    empty: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0,0,0,0.05)',
      padding: '40px 20px',
      textAlign: 'center',
      maxWidth: '600px',
      margin: '80px auto',
    }
  };

  return (
    <div style={styles.container}>
      {/* Coluna de filtros */}
      <div style={styles.filtrosCol}>
        <Filtro onFiltrar={aplicarFiltros} />
      </div>

      {/* Cards */}
      <div style={styles.conteudo}>
        <div style={styles.grid}>
          {receitasFiltradas.length === 0 ? (
            <div style={styles.empty}>
              <img src="/interrogacao.png" alt="Ícone vazio" width="60" />
              <h3>Nenhuma receita publicada ainda</h3>
              <p>Receitas aprovadas aparecerão aqui para o público em geral.</p>
            </div>
          ) : (
            receitasFiltradas.map(r => {
              const idReceita = r.id || r.id_conteudo;
              const imagemSrc = r.imagens?.[0]
                ? `http://localhost:5000${r.imagens[0]}`
                : null;

              return (
                <div key={idReceita} style={styles.card}>
                  <div style={styles.imagemContainer}>
                    {imagemSrc ? (
                      <img src={imagemSrc} alt={r.nomePlanta} style={styles.imagem} />
                    ) : (
                      <>
                        <img src="/interrogacao.png" alt="Sem imagem" width="40" />
                        <p>Sem Imagem</p>
                      </>
                    )}
                  </div>
                  <div style={styles.info}>
                    <h3 style={styles.nome}>{r.nomePlanta || 'Sem Título'}</h3>
                    <p style={styles.meta}>
                      <img src="/user.png" alt="autor" width="16" />
                      {r.autor || 'Anônimo'}
                    </p>
                    <p style={styles.meta}>
                      <img src="/calendar.png" alt="data" width="16" />
                      Publicado em: {new Date(r.data_publicacao).toLocaleDateString('pt-BR')}
                    </p>
                  </div>
                  <div style={styles.botoes}>
                    <BotaoAcao
                      label="Ver"
                      tipo="ver"
                      onClick={() => navigate(`/receita/${idReceita}`)}
                    />
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

export default Publicadas;
