import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import BotaoAcao from './BotaoAcao';
import Filtro from './FiltroEnviadas';

function Enviadas({ receitas }) {
  const navigate = useNavigate();
  const [filtros, setFiltros] = useState({
    status: '',
    id_categoria: '',
    id_epoca: '',
    id_solo: '',
    id_sol: ''
  });

  const excluirReceita = async (id) => {
    try {
      await axios.put(`http://localhost:5000/api/conteudos/${id}/excluir`);
      window.location.reload();
    } catch (err) {
      console.error('Erro ao excluir receita:', err);
    }
  };

  const aplicarFiltros = (f) => {
    setFiltros(f);
  };

  const receitasFiltradas = (receitas || []).filter(r => {
    // Lógica para filtrar apenas os itens com status "aprovado" e "statusAtivo"
    const isAprovadoEAtivo = r.status === 'aprovado' && r.statusAtivo === 1;
    if (!isAprovadoEAtivo) {
      return false;
    }

    const filtroStatus = filtros.status ? r.status === filtros.status : true;
    const filtroCategoria = filtros.id_categoria ? r.id_categoria == filtros.id_categoria : true;
    const filtroEpoca = filtros.id_epoca ? r.id_epoca == filtros.id_epoca : true;
    const filtroSolo = filtros.id_solo ? r.id_solo == filtros.id_solo : true;
    const filtroSol = filtros.id_sol ? r.id_sol == filtros.id_sol : true;

    return filtroStatus && filtroCategoria && filtroEpoca && filtroSolo && filtroSol;
  });

  return (
    <div className="enviadas-container flex">
      <div className="filtros-col">
        <Filtro onFiltrar={aplicarFiltros} />
      </div>

      <div className="receita-lista">
        {receitasFiltradas.length === 0 ? (
          <p className="mensagem-vazia">Nenhuma receita encontrada.</p>
        ) : (
          receitasFiltradas.map(r => {
            const id = r.id || r.id_conteudo;

            return (
              <div key={id} className="receita-card simples">
                <img
                  src={`http://localhost:5000${r.imagens?.[0] || '/uploads/no-image.png'}`}
                  alt={r.nomePlanta}
                  className="receita-imagem"
                  onError={e => e.currentTarget.src = 'http://localhost:5000/uploads/no-image.png'}
                />
                <h3>{r.nomePlanta}</h3>

                {r.status === 'pendente' && (
                  <>
                    <p className="aguardando">⏳ Aguardando moderação</p>
                    <div className="botoes-receita">
                      <BotaoAcao
                        label="Excluir"
                        tipo="excluir"
                        onClick={() => excluirReceita(id)}
                      />
                    </div>
                  </>
                )}

                {r.status === 'aprovado' && (
                  <>
                    <p className="texto-aprovado">✅ Aprovado</p>
                    <div className="botoes-receita flex gap-2">
                      <BotaoAcao
                        label="Ver"
                        tipo="ver"
                        onClick={() => navigate(`/receita/${id}`)}
                      />
                      <BotaoAcao
                        label="Excluir"
                        tipo="excluir"
                        onClick={() => excluirReceita(id)}
                      />
                    </div>
                  </>
                )}
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default Enviadas;
