import React from "react";
import Swal from "sweetalert2";
import "./BotaoAcao.css"; // Importa o novo arquivo CSS

function BotaoAcao({
  label,
  tipo = "neutro",
  tamanho = "padrao",
  icone,
  carregando = false,
  onClick,
}) {
  const handleClick = (e) => {
    e.preventDefault(); // Evita o comportamento padrão do botão
    if (carregando) return; // Não faz nada se o botão estiver em estado de carregamento
    
    onClick?.();

    // Evita popup para ações de navegação ou específicas
    if (tipo === "ver" || tipo === "voltar" || tipo === "neutro" || carregando) {
      return;
    }

    Swal.fire({
      icon: "success",
      title: "Alteração realizada!",
      text: `Ação "${label}" concluída com sucesso.`,
      showConfirmButton: true,
    });
  };

  // Define a classe CSS base e adiciona classes para tipo, tamanho e estado
  const classes = `btn-acao ${tipo} ${tamanho} ${icone ? 'com-icone' : ''} ${carregando ? 'carregando' : ''}`;

  return (
    <button className={classes} onClick={handleClick} disabled={carregando}>
      {carregando ? (
        <>
          <span className="spinner-border"></span>
          {label.replace('ando...', '').replace('ando', '')}ando...
        </>
      ) : (
        <>
          {icone && <img src={icone} alt="" className="btn-icone" />}
          {label}
        </>
      )}
    </button>
  );
}

export default BotaoAcao;
