import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import Topo from './Topo';
import './style.css';

function ReceitaDetalhe() {
  const { id } = useParams();
  const [receita, setReceita] = useState(null);
  const [favorita, setFavorita] = useState(false);

  const user = JSON.parse(localStorage.getItem('user'));
  const idUsuario = user?.id;

  useEffect(() => {
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => {
        setReceita(res.data);
        // Verifica se essa receita está nos favoritos do usuário
        return axios.get(`http://localhost:5000/api/favoritos/${idUsuario}`);
      })
      .then(res => {
        const favoritasDoUsuario = res.data; // array de receitas favoritas
        const isFavorita = favoritasDoUsuario.some(fav => fav.id_conteudo == id);
        setFavorita(isFavorita);
      })
      .catch(err => console.error(err));
  }, [id, idUsuario]);

  const toggleFavorito = () => {
    const url = `http://localhost:5000/api/favoritos/${idUsuario}/${id}`;
    if (favorita) {
      axios.delete(url).then(() => setFavorita(false));
    } else {
      axios.post(url).then(() => setFavorita(true));
    }
  };

  if (!receita) return <p>Carregando...</p>;

  return (
    <div className="pagina-detalhe">
      <Topo comMenu />

      <div className="receita-detalhe-container">
        <h1>{receita.nomePlanta}</h1>
        <p><strong>Categoria:</strong> {receita.categoria}</p>
        <p><strong>Plantio:</strong> {receita.epoca}</p>
        <p><strong>Temperatura:</strong> {receita.temperatura}</p>
        <p><strong>Solo:</strong> {receita.solo}</p>
        <p><strong>Rega:</strong> {receita.rega}</p>
        <p><strong>Sol:</strong> {receita.sol}</p>
        <p><strong>Instruções:</strong> {receita.instrucoes}</p>

        {/* Estrela de favorito no canto inferior direito */}
        <div className="estrela-favorito" onClick={toggleFavorito}>
          <img
            src={favorita ? "/star.png" : "/no-star.png"}
            alt="Favoritar"
            className="icone-estrela"
          />
        </div>
      </div>
    </div>
  );
}

export default ReceitaDetalhe;
