  cursor: 'pointer',
  fontSize: 18,
  fontWeight: 700
};

const legendItem = { display: 'flex', alignItems: 'center', gap: 8, color: '#556' };
const legendSwatch = { width: 16, height: 16, borderRadius: 4, border: '1px solid #e6ece7', display: 'inline-block' };
