import React from "react";
import Swal from "sweetalert2";

function BotaoAcao({ label, tipo = "padrao", onClick }) {
  const baseClasses =
    "px-4 py-2 rounded-lg text-sm font-medium transition-colors duration-200";

  const estilos = {
    ver: "bg-green-500 text-white hover:bg-green-600",
    aprovar: "bg-blue-500 text-white hover:bg-blue-600",
    rejeitar: "bg-yellow-500 text-white hover:bg-yellow-600",
    excluir: "bg-red-600 text-white hover:bg-red-700",
    padrao: "bg-gray-300 text-black hover:bg-gray-400",
  };

  const handleClick = () => {
    // Executa aÃ§Ã£o principal
    onClick?.();

    // ðŸš« Evita popup para Voltar ou Ver
    if (label.includes("Voltar") || label.includes("Ver")) return;

    // âœ… Mostra popup para os outros botÃµes
    Swal.fire({
      icon: "success",
      title: "AlteraÃ§Ã£o realizada!",
      text: `AÃ§Ã£o "${label}" concluÃ­da com sucesso.`,
      showConfirmButton: true,
    });
  };

  return (
    <button
      className={`${baseClasses} ${estilos[tipo] || estilos.padrao}`}
      onClick={handleClick}
    >
      {label}
    </button>
  );
}

export default BotaoAcao;
