import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";
import BotaoAcao from "../components/BotaoAcao";

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [receita, setReceita] = useState(null);

  const styles = {
    detalheContainer: {
      padding: "20px",
      backgroundColor: "#f0f4f7",
      minHeight: "100vh",
      fontFamily: "Arial, sans-serif",
    },
    detalheHeader: {
      textAlign: "center",
      marginBottom: "30px",
      paddingTop: "20px",
    },
    tituloDetalhe: {
      fontSize: "1.8rem",
      fontWeight: 700,
      color: "#3a414b",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "10px",
      margin: "0",
    },
    tituloIcon: {
      width: "30px",
      height: "30px",
    },
    card: {
      backgroundColor: "#fff",
      borderRadius: "12px",
      boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
      maxWidth: "800px",
      margin: "0 auto",
      padding: "20px",
      display: "flex",
      flexDirection: "column",
      gap: "15px",
    },
    imagem: {
      width: "100%",
      height: "300px",
      objectFit: "cover",
      borderRadius: "12px",
    },
    texto: {
      fontSize: "1rem",
      color: "#333",
      lineHeight: "1.6",
      whiteSpace: "pre-line", // üöÄ mant√©m quebras de linha
    },
    info: {
      fontSize: "0.95rem",
      color: "#555",
      display: "flex",
      alignItems: "center",
      gap: "8px",
    },
    infoIcon: {
      width: "18px",
      height: "18px",
    },
    botoesContainer: {
      display: "flex",
      flexWrap: "wrap",
      gap: "10px",
      justifyContent: "center",
      marginTop: "20px",
    },
  };

  useEffect(() => {
    axios
      .get(`http://localhost:5000/api/conteudos/${id}`)
      .then((res) => setReceita(res.data))
      .catch((err) => {
        console.error("Erro ao carregar receita:", err);
        Swal.fire({
          icon: "error",
          title: "Erro!",
          text: "N√£o foi poss√≠vel carregar os detalhes da receita.",
        });
      });
  }, [id]);

  if (!receita) {
    return (
      <div style={styles.detalheContainer}>
        <div style={styles.detalheHeader}>
          <h2 style={styles.tituloDetalhe}>Carregando receita...</h2>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.detalheContainer}>
      <div style={styles.detalheHeader}>
        <h2 style={styles.tituloDetalhe}>
          <img src="/list.png" alt="√çcone de receita" style={styles.tituloIcon} />
          Detalhes da Receita
        </h2>
      </div>

      <div style={styles.card}>
        {receita.imagens?.[0] ? (
          <img
            src={`http://localhost:5000${receita.imagens[0]}`}
            alt={receita.nomePlanta}
            style={styles.imagem}
          />
        ) : (
          <img src="/interrogacao.png" alt="Sem imagem" style={styles.imagem} />
        )}

        <h3>{receita.nomePlanta || "Sem t√≠tulo"}</h3>
        <p style={styles.info}>
          <img src="/user.png" alt="Autor" style={styles.infoIcon} />
          {receita.autor || "An√¥nimo"}
        </p>

        <p style={styles.texto}>{receita.instrucoes}</p>

        <div style={styles.botoesContainer}>
          <BotaoAcao label="Voltar" tipo="ver" onClick={() => navigate(-1)} />
          <BotaoAcao
            label="Aprovar"
            tipo="aprovar"
            onClick={() =>
              Swal.fire("‚úÖ Sucesso!", "Receita aprovada!", "success")
            }
          />
          <BotaoAcao
            label="Rejeitar"
            tipo="rejeitar"
            onClick={() =>
              Swal.fire("‚ö†Ô∏è Rejeitada!", "Receita rejeitada!", "warning")
            }
          />
          <BotaoAcao
            label="Excluir"
            tipo="excluir"
            onClick={() =>
              Swal.fire("üóëÔ∏è Exclu√≠da!", "Receita exclu√≠da!", "error")
            }
          />
        </div>
      </div>
    </div>
  );
}

export default ReceitaDetalhe;
