import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BotaoAcao from './BotaoAcao';
import Filtro from './FiltroEnviadas';

function Publicadas({ receitas }) {
  const navigate = useNavigate();

  const [filtros, setFiltros] = useState({
    status: '',
    id_categoria: '',
    id_epoca: '',
    id_solo: '',
    id_sol: ''
  });

  const aplicarFiltros = (f) => {
    setFiltros(f);
  };

  const receitasAprovadas = (receitas || []).filter(r => r.status === 'aprovado');

  const receitasFiltradas = receitasAprovadas.filter(r => {
    const filtroCategoria = filtros.id_categoria ? r.id_categoria == filtros.id_categoria : true;
    const filtroEpoca = filtros.id_epoca ? r.id_epoca == filtros.id_epoca : true;
    const filtroSolo = filtros.id_solo ? r.id_solo == filtros.id_solo : true;
    const filtroSol = filtros.id_sol ? r.id_sol == filtros.id_sol : true;

    return filtroCategoria && filtroEpoca && filtroSolo && filtroSol;
  });

  const styles = {
    container: {
      display: 'flex',
      gap: '20px',
      padding: '20px',
      minHeight: '100vh',
      fontFamily: 'Arial, sans-serif',
      backgroundColor: '#f3f6f9',
      width: '80%',
      margin: '0 auto',
    },
    filtrosCol: {
      flexShrink: 0,
      width: '260px',
      backgroundColor: '#fff',
      borderRadius: '12px',
      padding: '20px',
      boxShadow: '0 2px 8px rgba(0,0,0,0.08)',
      height: 'fit-content',
      position: 'sticky',
      top: '20px',
    },
    conteudo: {
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
    },
    receitaListaGrid: {
      display: 'grid',
      gridTemplateColumns: 'repeat(3, minmax(200px, 1fr))',
      gap: '20px',
    },
    receitaCard: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)',
      display: 'flex',
      flexDirection: 'column',
      minHeight: '350px',
      padding: '15px',
      textAlign: 'center',
    },
    receitaImagem: {
      width: '100%',
      height: '150px',
      objectFit: 'cover',
      borderRadius: '8px',
      marginBottom: '15px',
    },
    receitaNome: {
      fontSize: '1.25rem',
      fontWeight: 'bold',
      color: '#333',
      margin: '0 0 10px 0',
    },
    receitaInfo: {
      fontSize: '0.9rem',
      color: '#666',
      marginBottom: '8px',
    },
    emptyStateContainer: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
      padding: '40px 20px',
      textAlign: 'center',
      maxWidth: '600px',
      margin: '80px auto',
    },
    emptyStateTitle: {
      fontSize: '1.5rem',
      fontWeight: 600,
      color: '#4a4a4a',
      marginBottom: '10px',
    },
    emptyStateText: {
      fontSize: '1rem',
      color: '#777',
      maxWidth: '400px',
      margin: '0 auto',
    },
    // Estilos para a exibição de "Sem Imagem"
    noImageContainer: {
      backgroundColor: '#2d6a4f', // Cor de fundo do Moderacao
      borderRadius: '8px',
      width: '100%',
      height: '150px',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      marginBottom: '15px',
      color: 'white',
      padding: '10px',
      boxSizing: 'border-box'
    },
    noImageIcon: {
      width: '40px',
      height: '40px',
      marginBottom: '10px',
    },
    noImageText: {
      margin: 0,
      fontSize: '1.1rem',
      fontWeight: 'bold'
    }
  };

  return (
    <div style={styles.container}>
      {/* Coluna de Filtros */}
      <div style={styles.filtrosCol}>
        <Filtro onFiltrar={aplicarFiltros} />
      </div>

      {/* Conteúdo (lista de cards) */}
      <div style={styles.conteudo}>
        <div style={styles.receitaListaGrid}>
          {receitasFiltradas.length === 0 ? (
            <div style={styles.emptyStateContainer}>
              <h3 style={styles.emptyStateTitle}>Nenhuma receita publicada ainda.</h3>
              <p style={styles.emptyStateText}>
                Receitas aprovadas aparecerão aqui para o público em geral.
              </p>
            </div>
          ) : (
            receitasFiltradas.map(r => {
              const dataFormatada = new Date(r.data_publicacao).toLocaleDateString('pt-BR');
              const hasImage = r.imagens?.[0];

              return (
                <div key={r.id || r.id_conteudo} style={styles.receitaCard}>
                  {hasImage ? (
                    <img
                      src={`http://localhost:5000${r.imagens[0]}`}
                      alt={r.nomePlanta}
                      style={styles.receitaImagem}
                    />
                  ) : (
                    <div style={styles.noImageContainer}>
                      <img src="/interrogacao.png" alt="Sem imagem" style={styles.noImageIcon} />
                      <p style={styles.noImageText}>Sem Imagem</p>
                    </div>
                  )}
                  <h3 style={styles.receitaNome}>{r.nomePlanta}</h3>
                  <p style={styles.receitaInfo}><strong>Autor:</strong> {r.autor || 'Anônimo'}</p>
                  <p style={styles.receitaInfo}><strong>Publicado em:</strong> {dataFormatada}</p>
                  <div style={{ marginTop: 'auto' }}>
                    <BotaoAcao
                      label="Ver receita"
                      tipo="ver"
                      onClick={() => navigate(`/receita/${r.id || r.id_conteudo}`)}
                    />
                  </div>
                </div>
              );
            })
          )}
        </div>
      </div>
    </div>
  );
}

export default Publicadas;
