import React, { useState } from "react";
import "../styles/FiltroEnviadas.css";

const FiltroEnviadas = ({ onFiltrar }) => {
  // Opções criadas manualmente para evitar a dependência da API
  const [categorias] = useState([
    { id: 1, nome: "Flor" },
    { id: 2, nome: "Fruta" },
    { id: 3, nome: "Legume" },
    { id: 4, nome: "Verdura" },
    { id: 5, nome: "Erva" },
    { id: 6, nome: "Raiz" },
    { id: 7, nome: "Cereal" },
    { id: 8, nome: "Trepadeira" }
  ]);
  const [epocas] = useState([
    { id: 1, nome: "Primavera" },
    { id: 2, nome: "Verão" },
    { id: 3, nome: "Outono" },
    { id: 4, nome: "Inverno" },
    { id: 5, nome: "Ano todo" },
    { id: 6, nome: "Estação seca" },
    { id: 7, nome: "Estação chuvosa" }
  ]);
  const [solos] = useState([
    { id: 1, nome: "Arenoso" },
    { id: 2, nome: "Argiloso" },
    { id: 3, nome: "Siltoso" },
    { id: 4, nome: "Humoso" },
    { id: 5, nome: "Calcário" },
    { id: 6, nome: "Gessado" },
    { id: 7, nome: "Alagado" },
    { id: 8, nome: "Mistura de solo (composto)" }
  ]);
  const [sóis] = useState([
    { id: 1, nome: "Pleno sol" },
    { id: 2, nome: "Meia sombra" },
    { id: 3, nome: "Sombra" },
    { id: 4, nome: "Sol pleno o dia todo" },
    { id: 5, nome: "Sol da manhã e sombra à tarde" },
    { id: 6, nome: "Sombra total" }
  ]);

  const [filtros, setFiltros] = useState({
    id_categoria: "",
    id_epoca: "",
    id_solo: "",
    id_sol: ""
  });

  const [abertos, setAbertos] = useState({
    id_categoria: false,
    id_epoca: false,
    id_solo: false,
    id_sol: false
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFiltros({
      ...filtros,
      [name]: value
    });
  };

  const aplicarFiltros = () => {
    onFiltrar(filtros);
  };

  const limparFiltros = () => {
    const reset = { id_categoria: "", id_epoca: "", id_solo: "", id_sol: "" };
    setFiltros(reset);
    onFiltrar(reset);
  };

  const toggleAberto = (nome) => {
    setAbertos(prev => ({
      ...prev,
      [nome]: !prev[nome]
    }));
  };

  const renderSelect = (name, label, options, idKey, nameKey) => (
    <div className="filtro-item">
      <div className="filtro-header" onClick={() => toggleAberto(name)}>
        <h4>{label}</h4>
        <span>{abertos[name] ? '▲' : '▼'}</span>
      </div>
      {abertos[name] && (
        <div className="filtro-opcoes">
          <label className="radio-label">
            <input
              type="radio"
              name={name}
              value=""
              checked={filtros[name] === ""}
              onChange={handleChange}
            />
            Todos
          </label>
          {Array.isArray(options) && options.map(o => (
            <label key={o[idKey]} className="radio-label">
              <input
                type="radio"
                name={name}
                value={o[idKey]}
                checked={filtros[name] == o[idKey]}
                onChange={handleChange}
              />
              {o[nameKey]}
            </label>
          ))}
        </div>
      )}
    </div>
  );

  return (
    <div className="filtro-enviadas-container">
      <h3>Filtrar Receitas</h3>
      {renderSelect("id_categoria", "Tipo de Produto", categorias, "id", "nome")}
      {renderSelect("id_epoca", "Época", epocas, "id", "nome")}
      {renderSelect("id_solo", "Solo", solos, "id", "nome")}
      {renderSelect("id_sol", "Tipo de Sol", sóis, "id", "nome")}

      <div className="botoes-filtro">
        <button className="aplicar-btn" onClick={aplicarFiltros}>Aplicar</button>
        <button className="limpar-btn" onClick={limparFiltros}>Limpar</button>
      </div>
    </div>
  );
};

export default FiltroEnviadas;
