import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Topo from '../components/Topo'; // Assumindo que você usa o Topo em todas as páginas

function Perfil() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [editando, setEditando] = useState(false);
  const [mensagem, setMensagem] = useState('');
  const navigate = useNavigate();

  // Carrega os dados do usuário ao carregar a página
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      setNome(user.nome);
      setEmail(user.email);
    }
  }, []);

  const handleSalvar = (e) => {
    e.preventDefault();
    setMensagem('');

    // Simulação da atualização no localStorage
    const user = JSON.parse(localStorage.getItem('user'));
    if (user) {
      const usuarioAtualizado = { ...user, nome, email };
      localStorage.setItem('user', JSON.stringify(usuarioAtualizado));
      setEditando(false);
      setMensagem('Informações atualizadas com sucesso!');
    }
  };

  const deslogar = () => {
    localStorage.clear();
    navigate('/');
  };

  const handleVoltar = () => {
    navigate('/home');
  };

  return (
    <div style={styles.pagina}>
      <Topo />
      <div style={styles.container}>
        <button onClick={handleVoltar} style={styles.botaoVoltar}>&larr; Voltar</button>
        <div style={styles.card}>
          <h2 style={styles.titulo}>{editando ? 'Editar Perfil' : 'Meu Perfil'}</h2>
          
          {mensagem && <p style={styles.mensagemSucesso}>{mensagem}</p>}

          <div style={styles.userInfo}>
            <p><strong>Nome:</strong> {editando ? (
              <input 
                type="text" 
                value={nome} 
                onChange={(e) => setNome(e.target.value)} 
                style={styles.input}
              />
            ) : nome}</p>
            <p><strong>Email:</strong> {editando ? (
              <input 
                type="email" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                style={styles.input}
              />
            ) : email}</p>
          </div>
          
          <div style={styles.botoes}>
            {editando ? (
              <button onClick={handleSalvar} style={styles.botaoSalvar}>Salvar</button>
            ) : (
              <button onClick={() => setEditando(true)} style={styles.botaoEditar}>Editar</button>
            )}
            <button onClick={deslogar} style={styles.botaoSair}>Sair</button>
          </div>
        </div>
      </div>
    </div>
  );
}

const styles = {
    pagina: {
        display: 'flex',
        flexDirection: 'column',
        minHeight: '100vh',
        backgroundColor: '#f1f1f1',
        paddingTop: '60px',
    },
    container: {
        flex: 1,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '20px',
    },
    botaoVoltar: {
      alignSelf: 'flex-start',
      marginLeft: '20px',
      padding: '10px 15px',
      backgroundColor: '#2d6a4f',
      color: '#fff',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      fontSize: '1rem',
      fontWeight: 'bold',
      marginBottom: '20px',
    },
    card: {
        backgroundColor: '#fff',
        padding: '40px',
        borderRadius: '15px',
        boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        textAlign: 'center',
        width: '100%',
        maxWidth: '500px',
    },
    titulo: {
        fontSize: '2rem',
        marginBottom: '20px',
        color: '#2d6a4f',
    },
    userInfo: {
      textAlign: 'left',
      marginBottom: '20px',
      fontSize: '1.2rem',
    },
    input: {
      width: '100%',
      padding: '8px',
      marginTop: '5px',
      borderRadius: '5px',
      border: '1px solid #ccc',
      fontSize: '1rem',
    },
    botoes: {
      display: 'flex',
      justifyContent: 'space-around',
      marginTop: '20px',
      gap: '15px'
    },
    botaoEditar: {
        padding: '12px 24px',
        backgroundColor: '#4a7c59',
        color: '#fff',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontSize: '1rem',
        fontWeight: 'bold',
        transition: 'background-color 0.3s',
        flex: 1,
    },
    botaoSalvar: {
        padding: '12px 24px',
        backgroundColor: '#2d6a4f',
        color: '#fff',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontSize: '1rem',
        fontWeight: 'bold',
        transition: 'background-color 0.3s',
        flex: 1,
    },
    botaoSair: {
        padding: '12px 24px',
        backgroundColor: '#d62828',
        color: '#fff',
        border: 'none',
        borderRadius: '8px',
        cursor: 'pointer',
        fontSize: '1rem',
        fontWeight: 'bold',
        transition: 'background-color 0.3s',
        flex: 1,
    },
    mensagemSucesso: {
      color: '#2d6a4f',
      backgroundColor: '#d4edda',
      border: '1px solid #c3e6cb',
      padding: '10px',
      borderRadius: '5px',
      marginBottom: '15px',
      textAlign: 'center',
    }
};

export default Perfil;
