import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './style.css';

function Conteudo() {
  const [menuAberto, setMenuAberto] = useState(false);
  const [notificacoes, setNotificacoes] = useState([]);
  const [receitas, setReceitas] = useState([]);
  const [mostrarFormulario, setMostrarFormulario] = useState(false);
  const [filtro, setFiltro] = useState('todas');

  const [nomePlanta, setNomePlanta] = useState('');
  const [categoria, setCategoria] = useState('');
  const [epoca, setEpoca] = useState('');
  const [temperatura, setTemperatura] = useState('');
  const [solo, setSolo] = useState('');
  const [rega, setRega] = useState('');
  const [sol, setSol] = useState('');
  const [instrucoes, setInstrucoes] = useState('');

  const navigate = useNavigate();
  const idUsuario = 1;
  const nomeAutor = localStorage.getItem('nomeUsuario') || 'Anônimo';

  useEffect(() => {
    axios.get(`http://localhost:5000/api/notificacoes/${idUsuario}`)
      .then(res => {
        const novas = res.data.filter(n => !n.visualizado);
        setNotificacoes(novas);
      })
      .catch(err => console.error('Erro ao buscar notificações:', err));
  }, []);

  useEffect(() => {
    axios.get('http://localhost:5000/api/conteudos')
      .then(res => setReceitas(res.data))
      .catch(err => console.error('Erro ao buscar receitas:', err));
  }, []);

  const deslogar = () => {
    localStorage.removeItem('usuarioLogado');
    window.location.href = '/';
  };

  const postarReceita = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/conteudos', {
        nomePlanta,
        categoria,
        epoca,
        temperatura,
        solo,
        rega,
        sol,
        instrucoes,
        autor: nomeAutor
      });
      setNomePlanta('');
      setCategoria('');
      setEpoca('');
      setTemperatura('');
      setSolo('');
      setRega('');
      setSol('');
      setInstrucoes('');
      setMostrarFormulario(false);

      const res = await axios.get('http://localhost:5000/api/conteudos');
      setReceitas(res.data);
    } catch (error) {
      console.error('Erro ao postar receita:', error);
    }
  };

  return (
    <div className="home-container">
      <div className="top-bar">
        <img src="/menu.png" alt="Menu" className="menu-icon" onClick={() => setMenuAberto(!menuAberto)} />
        <img src="/logo.png" alt="Logo AgroPlanner" className="logo-topo" />
        <div className="notificacao-icone">
          <img src={notificacoes.length > 0 ? "/notification-yes.png" : "/notification-no.png"} alt="Notificações" className="notificacao-img" />
          {notificacoes.length > 0 && <span className="notificacao-contador">{notificacoes.length}</span>}
        </div>
      </div>

      {menuAberto && (
        <div className="menu-lateral">
          <button onClick={deslogar}>Sair</button>
        </div>
      )}

      <div className="botoes-superiores">
        <button onClick={() => navigate('/home')}>
          <img src="/back.png" alt="Voltar" />
          <span>Voltar</span>
        </button>

        <button onClick={() => setFiltro('favoritas')}>
          <img src="/star.png" alt="Favoritas" />
          <span>Favoritas</span>
        </button>

        <button onClick={() => setFiltro('publicadas')}>
          <img src="/cook-book.png" alt="Publicadas" />
          <span>Publicadas</span>
        </button>

        <button onClick={() => setFiltro('enviadas')}>
          <img src="/recipe-sent.png" alt="Enviadas" />
          <span>Enviadas</span>
        </button>

        <button onClick={() => setMostrarFormulario(!mostrarFormulario)}>
          <img src="/add.png" alt="Adicionar" />
          <span>{mostrarFormulario ? 'Cancelar' : 'Adicionar'}</span>
        </button>
      </div>

      {mostrarFormulario && (
        <form className="formulario" onSubmit={postarReceita}>
          <label>Nome da planta:</label>
          <input value={nomePlanta} onChange={e => setNomePlanta(e.target.value)} required />

          <label>Categoria:</label>
          <select value={categoria} onChange={e => setCategoria(e.target.value)} required>
            <option value="">Selecione</option>
            <option value="flor">Flor</option>
            <option value="fruta">Fruta</option>
            <option value="legume">Legume</option>
          </select>

          <label>Época ideal de plantio:</label>
          <select value={epoca} onChange={e => setEpoca(e.target.value)} required>
            <option value="">Selecione</option>
            <option value="primavera">Primavera</option>
            <option value="verao">Verão</option>
            <option value="outono">Outono</option>
            <option value="inverno">Inverno</option>
          </select>

          {epoca && (
            <>
              <label>Mês de plantio:</label>
              <select required>
                <option value="">Selecione</option>
                {epoca === 'primavera' && <>
                  <option>Setembro</option>
                  <option>Outubro</option>
                  <option>Novembro</option>
                </>}
                {epoca === 'verao' && <>
                  <option>Dezembro</option>
                  <option>Janeiro</option>
                  <option>Fevereiro</option>
                </>}
                {epoca === 'outono' && <>
                  <option>Março</option>
                  <option>Abril</option>
                  <option>Maio</option>
                </>}
                {epoca === 'inverno' && <>
                  <option>Junho</option>
                  <option>Julho</option>
                  <option>Agosto</option>
                </>}
              </select>
            </>
          )}

          <label>Temperatura ideal (°C):</label>
          <input type="number" value={temperatura} onChange={e => setTemperatura(e.target.value)} />

          <label>Tipo de solo:</label>
          <input value={solo} onChange={e => setSolo(e.target.value)} />

          <label>Frequência de rega:</label>
          <div style={{ display: 'flex', gap: '10px' }}>
            <input
              type="number"
              min="1"
              placeholder="Quantidade"
              value={rega}
              onChange={e => setRega(e.target.value)}
            />
            <select value={sol} onChange={e => setSol(e.target.value)} required>
              <option value="">Unidade</option>
              <option value="dia">por dia</option>
              <option value="semana">por semana</option>
              <option value="mês">por mês</option>
            </select>
          </div>

          <label>Exposição ao sol:</label>
          <select value={sol} onChange={e => setSol(e.target.value)} required>
            <option value="">Selecione</option>
            <option value="pleno sol">Pleno sol – mínimo de 4h de sol direto</option>
            <option value="meia sombra">Meia sombra – poucas horas de sol, boa luz</option>
            <option value="sombra">Sombra – luz indireta, sem sol direto</option>
          </select>

          <label>Instruções de plantio:</label>
          <textarea value={instrucoes} onChange={e => setInstrucoes(e.target.value)} required />

          <button type="submit">Postar Receita</button>
        </form>
      )}

      <div className="receita-lista">
        {receitas
          .filter(r => {
            if (filtro === 'favoritas') return r.favorita;
            if (filtro === 'publicadas') return r.autor === nomeAutor;
            if (filtro === 'enviadas') return r.status === 'pendente';
            return true;
          })
          .map((r) => (
            <div key={r.id} className="receita-card">
              <h3>{r.nomePlanta}</h3>
              <p><strong>Categoria:</strong> {r.categoria}</p>
              <p><strong>Plantio:</strong> {r.epoca}</p>
              <p><strong>Temperatura:</strong> {r.temperatura}</p>
              <p><strong>Solo:</strong> {r.solo}</p>
              <p><strong>Rega:</strong> {r.rega}</p>
              <p><strong>Sol:</strong> {r.sol}</p>
              <p>{r.instrucoes}</p>
              <small>Por {r.autor} em {new Date(r.data_publicacao).toLocaleDateString()}</small>
            </div>
          ))}
      </div>
    </div>
  );
}

export default Conteudo;
