import React, { useEffect, useState } from "react";
import axios from "axios";
import Swal from "sweetalert2";
import { useNavigate } from "react-router-dom";
import BotaoAcao from "./BotaoAcao";

function Favoritas() {
  const [favoritos, setFavoritos] = useState([]);
  const user = JSON.parse(localStorage.getItem("user"));
  const idUsuario = user?.id;
  const navigate = useNavigate();

  const styles = {
    favoritasContainer: {
      padding: "20px",
      backgroundColor: "#f0f4f7",
      minHeight: "100vh",
      fontFamily: "Arial, sans-serif",
    },
    favoritasHeader: {
      textAlign: "center",
      marginBottom: "30px",
      paddingTop: "20px",
    },
    tituloFavoritas: {
      fontSize: "1.8rem",
      fontWeight: 700,
      color: "#3a414b",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      gap: "10px",
      margin: "0",
    },
    tituloIcon: {
      width: "30px",
      height: "30px",
    },
    receitaListaGrid: {
      display: "grid",
      gridTemplateColumns: "repeat(auto-fit, minmax(280px, 1fr))",
      gap: "20px",
      justifyContent: "center",
      maxWidth: "1200px",
      margin: "0 auto",
    },
    receitaCard: {
      backgroundColor: "#fff",
      borderRadius: "12px",
      boxShadow: "0 4px 12px rgba(0, 0, 0, 0.08)",
      overflow: "hidden",
      display: "flex",
      flexDirection: "column",
      minHeight: "400px",
    },
    receitaImagemContainer: {
      width: "100%",
      height: "180px",
      overflow: "hidden",
      backgroundColor: "#2d6a4f",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      flexShrink: 0,
      flexDirection: "column",
      color: "#fff",
      fontWeight: 600,
      textAlign: "center",
    },
    receitaImagem: {
      width: "100%",
      height: "100%",
      objectFit: "cover",
    },
    noImageIcon: {
      width: "40px",
      height: "40px",
      marginBottom: "10px",
    },
    receitaInfo: {
      padding: "15px",
      flexGrow: 1,
      display: "flex",
      flexDirection: "column",
    },
    receitaId: {
      fontSize: "0.85rem",
      color: "#a0a0a0",
      marginBottom: "5px",
    },
    receitaNome: {
      fontSize: "1.1rem",
      fontWeight: 700,
      color: "#333",
      margin: "0 0 10px 0",
      lineHeight: "1.3",
    },
    receitaAutor: {
      fontSize: "0.9rem",
      color: "#666",
      display: "flex",
      alignItems: "center",
      gap: "8px",
      margin: "0 0 15px 0",
    },
    infoIcon: {
      width: "16px",
      height: "16px",
    },
    botoesContainer: {
      padding: "0 15px 15px",
      display: "flex",
      flexWrap: "wrap",
      gap: "8px",
      justifyContent: "center",
      marginTop: "auto",
    },
    emptyStateContainer: {
      backgroundColor: "#fff",
      borderRadius: "12px",
      boxShadow: "0 4px 12px rgba(0, 0, 0, 0.05)",
      padding: "40px 20px",
      textAlign: "center",
      maxWidth: "600px",
      margin: "80px auto",
    },
    emptyStateIconWrapper: {
      width: "80px",
      height: "80px",
      margin: "0 auto 20px",
      border: "4px solid #b0c9b0",
      borderRadius: "50%",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    },
    emptyStateIcon: {
      width: "50px",
      height: "50px",
    },
    emptyStateTitle: {
      fontSize: "1.5rem",
      fontWeight: 600,
      color: "#4a4a4a",
      marginBottom: "10px",
    },
    emptyStateText: {
      fontSize: "1rem",
      color: "#777",
      maxWidth: "400px",
      margin: "0 auto",
    },
  };

  useEffect(() => {
    if (!idUsuario) return;
    carregarFavoritos();
  }, [idUsuario]);

  const carregarFavoritos = () => {
    axios
      .get(`http://localhost:5000/api/favoritos/${idUsuario}`)
      .then((res) => setFavoritos(res.data))
      .catch((err) => console.error("Erro ao carregar favoritos:", err));
  };

  const removerFavorito = async (id_conteudo) => {
    try {
      await axios.put("http://localhost:5000/api/favoritos/remover", {
        id_usuario: idUsuario,
        id_conteudo,
      });
      setFavoritos((prev) => prev.filter((r) => r.id !== id_conteudo));
      Swal.fire({
        icon: "success",
        title: "Removido!",
        text: "Receita removida dos favoritos.",
        timer: 1500,
        showConfirmButton: false,
      });
    } catch (err) {
      console.error("Erro ao remover favorito:", err);
      Swal.fire({
        icon: "error",
        title: "Erro!",
        text: "Não foi possível remover dos favoritos.",
      });
    }
  };

  return (
    <div style={styles.favoritasContainer}>
      <div style={styles.favoritasHeader}>
        <h2 style={styles.tituloFavoritas}>
          <img src="/heart.png" alt="Ícone de coração" style={styles.tituloIcon} />
          Minhas Receitas Favoritas
        </h2>
      </div>

      {favoritos.length === 0 ? (
        <div style={styles.emptyStateContainer}>
          <div style={styles.emptyStateIconWrapper}>
            <img src="/heart.png" alt="Ícone de coração vazio" style={styles.emptyStateIcon} />
          </div>
          <h3 style={styles.emptyStateTitle}>Nenhuma receita favoritada</h3>
          <p style={styles.emptyStateText}>
            Você ainda não favoritou nenhuma receita.  
            Quando marcar suas preferidas, elas aparecerão aqui.
          </p>
        </div>
      ) : (
        <div style={styles.receitaListaGrid}>
          {favoritos.map((r) => {
            const idReceita = r.id || r.id_conteudo;
            const imagemSrc = r.imagens
              ? `http://localhost:5000${r.imagens.split(",")[0]}`
              : null;

            return (
              <div key={idReceita} style={styles.receitaCard}>
                <div style={styles.receitaImagemContainer}>
                  {imagemSrc ? (
                    <img src={imagemSrc} alt={r.nomePlanta} style={styles.receitaImagem} />
                  ) : (
                    <>
                      <img src="/interrogacao.png" alt="Sem imagem" style={styles.noImageIcon} />
                      <p>Sem Imagem</p>
                    </>
                  )}
                </div>
                <div style={styles.receitaInfo}>
                  <div style={styles.receitaId}>ID: {idReceita}</div>
                  <h3 style={styles.receitaNome}>{r.nomePlanta || "Sem Título"}</h3>
                  <p style={styles.receitaAutor}>
                    <img src="/user.png" alt="Ícone de usuário" style={styles.infoIcon} />
                    {r.autor || "Anônimo"}
                  </p>
                </div>
                <div style={styles.botoesContainer}>
                  <BotaoAcao
                    label="Ver"
                    tipo="ver"
                    onClick={() => navigate(`/receita/${idReceita}`)}
                  />
                  <BotaoAcao
                    label="Remover"
                    tipo="remover"
                    onClick={() => removerFavorito(idReceita)}
                  />
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

export default Favoritas;
