import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Topo from '../components/Topo';
import '../styles/style.css';

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [menuAberto, setMenuAberto] = useState(false);
  const [receita, setReceita] = useState(null);

  const user = JSON.parse(localStorage.getItem('user'));
  const idUsuario = user?.id;
  const isModerador = user?.role === 'admin';

  useEffect(() => {
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => setReceita(res.data))
      .catch(err => console.error('Erro ao buscar receita:', err));
  }, [id]);

  const atualizarStatus = async (status) => {
    try {
      await axios.put(`http://localhost:5000/api/conteudos/${id}/status`, { status });
      navigate('/moderacao');
    } catch (err) {
      console.error('Erro ao atualizar status:', err);
    }
  };

  if (!receita) return <div>Carregando receita...</div>;

  const dataFormatada = new Date(receita.data_publicacao).toLocaleDateString('pt-BR');

  return (
    <div className="receita-detalhe-container">
      <Topo centralizado comMenu onMenuClick={() => setMenuAberto(!menuAberto)} />
      {menuAberto && (
        <div className="menu-lateral">
          <button onClick={() => navigate('/')}>Início</button>
          <button onClick={() => localStorage.clear() || navigate('/')}>Sair</button>
        </div>
      )}

      <h1 className="receita-titulo" style="margin">🌿 {receita.nomePlanta}</h1>

      {receita.imagens?.length > 0 ? (
        <img
          src={`http://localhost:5000${receita.imagens[0]}`}
          alt={receita.nomePlanta}
          className="receita-imagem-grande"
        />
      ) : (
        <img
          src="http://localhost:5000/uploads/no-image.png"
          alt="Sem imagem"
          className="receita-imagem-grande"
        />
      )}

      <div className="receita-info">
        <p>
          <strong>
            <img
              src="/user.png"
              alt="Autor"
              className="autor-imagem"
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Autor:
          </strong> {receita.autor || 'Anônimo'}
        </p>


        <p><strong>📅 Publicada em:</strong> {dataFormatada}</p>
        <hr />

        <p>
          <strong>
            <img
              src={`/categoria/${receita.id_categoria}.png`}
              alt={receita.categoria}
              className="categoria-imagem"
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Categoria:
          </strong> {receita.categoria}
        </p>

        <p>
          <strong>
            <img
              src={`/estacao/${receita.id_epoca}.png`}
              alt={receita.epoca}
              className="estacao-imagem"
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Época de plantio:
          </strong> {receita.epoca}
        </p>

        <p>
          <strong>
            <img
              src={
                Number(receita.temperatura) < 20
                  ? '/frio.png'
                  : '/quente.png'
              }
              alt="Temperatura"
              className="temperatura-imagem"
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Temperatura ideal:
          </strong> {receita.temperatura}°C
        </p>

        <p>
          <strong>
            <img
              src={`/solo/${receita.id_solo}.png`}
              alt={receita.solo}
              className="solo-imagem"
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Solo:
          </strong> {receita.solo}
        </p>

        <p>
          <strong>
            <img
              src={
                receita.rega?.toLowerCase().includes('dia') ? '/gota/3.png' :
                  receita.rega?.toLowerCase().includes('semana') ? '/gota/2.png' :
                    receita.rega?.toLowerCase().includes('mês') || receita.rega?.toLowerCase().includes('mes') ? '/gota/1.png' :
                      '/gota/1.png'
              }
              alt="Frequência de rega"
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Frequência de rega:
          </strong> {receita.rega}
        </p>

        <p>
          <strong>
            <img
              src={`/sol/${receita.id_sol}.png`}
              alt={receita.sol}
              style={{ width: '30px', marginBottom: '-8px', marginRight: '10px' }}
            />
            Exposição ao sol:
          </strong> {receita.sol}
        </p>

        <p><strong>📋 Instruções detalhadas:</strong></p>
        <div style={{ backgroundColor: '#f1f1f1', padding: '1rem', borderRadius: '10px' }}>
          <p>{receita.instrucoes}</p>
        </div>
      </div>

      <div className="botoes-aprovacao">
        <button className="btn-voltar-especial" onClick={() => navigate(-1)}>← Voltar</button>

        {isModerador && (
          <>
            <button className="btn-aprovar" onClick={() => atualizarStatus('aprovado')}>✅ Aprovar</button>
            <button className="btn-rejeitar" onClick={() => atualizarStatus('rejeitado')}>❌ Rejeitar</button>
          </>
        )}
      </div>
    </div>
  );
}

export default ReceitaDetalhe;
