import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/style.css'; // Assuming you have a CSS file for general styles

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [receita, setReceita] = useState(null);

  useEffect(() => {
    // You might want to mock this data to match the image until you have the real API endpoint
    // This fetch is kept for demonstration purposes
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => setReceita(res.data))
      .catch(err => console.error('Erro ao buscar receita:', err));
  }, [id]);

  if (!receita) {
    return <div style={{ textAlign: 'center', padding: '20px' }}>Carregando...</div>;
  }

  // Helper function to format the date as dd/mm/yyyy
  const dataFormatada = new Date(receita.data_publicacao).toLocaleDateString('pt-BR');

  // The content for the Cultivation Conditions and Care sections
  const condicoesDeCultivo = [
    { label: '√âpoca de plantio', value: 'Outono', icon: 'üìÖ' },
    { label: 'Temperatura ideal', value: '6¬∞C', icon: 'üå°Ô∏è' },
    { label: 'Solo', value: 'H√∫mido', icon: 'ü™®' },
  ];

  const cuidados = [
    { label: 'Frequ√™ncia de rega', value: '6 dias', icon: 'üíß' },
    { label: 'Exposi√ß√£o ao sol', value: 'Meia sombra - algumas horas com luz direta', icon: '‚òÄÔ∏è' },
    { label: 'Instru√ß√µes detalhadas', value: '#787543', icon: 'üìã' },
  ];

  return (
    <div style={{
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      padding: '20px',
      backgroundColor: '#f5f5f5' // A light gray background to match the image
    }}>
      <div style={{
        width: '100%',
        maxWidth: '800px',
        backgroundColor: '#fff',
        borderRadius: '16px',
        boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
        overflow: 'hidden',
        fontFamily: 'sans-serif'
      }}>
        {/* Top Header Section */}
        <div style={{
          position: 'relative',
          backgroundColor: '#4d985a', // A deep green color
          padding: '20px',
          color: '#fff',
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'flex-end',
          borderRadius: '16px 16px 0 0'
        }}>
          {/* ID at the top right */}
          <div style={{
            fontSize: '14px',
            backgroundColor: 'rgba(255,255,255,0.2)',
            padding: '4px 12px',
            borderRadius: '20px',
            marginBottom: '10px'
          }}>
            ID: {id}
          </div>

          <div style={{
            display: 'flex',
            alignItems: 'center',
            width: '100%'
          }}>
            {/* Plant Image */}
            <div style={{
              width: '120px',
              height: '120px',
              backgroundColor: '#fff',
              borderRadius: '8px',
              overflow: 'hidden',
              flexShrink: 0
            }}>
              <img
                src={receita.imagens?.[0] || 'http://localhost:5000/uploads/no-image.png'}
                alt={receita.nomePlanta}
                style={{ width: '100%', height: '100%', objectFit: 'cover' }}
              />
            </div>

            {/* Title and Author Info */}
            <div style={{ marginLeft: '20px', display: 'flex', flexDirection: 'column', gap: '8px', flexGrow: 1 }}>
              <h1 style={{ fontSize: '24px', margin: 0, fontWeight: 'bold' }}>{receita.nomePlanta || 'Legume'}</h1>
              <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                <span style={{ fontSize: '18px' }}>üë§</span>
                <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Autor</span>
                <span style={{ fontSize: '16px' }}>{receita.autor || 'Jo√£o Vin√≠cius Letzuk'}</span>
              </div>
              <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
                <span style={{ fontSize: '18px' }}>üìÖ</span>
                <span style={{ fontSize: '16px', fontWeight: 'bold' }}>Publicada em</span>
                <span style={{ fontSize: '16px' }}>{dataFormatada}</span>
              </div>
              <div style={{
                fontSize: '14px',
                backgroundColor: '#2d6a4f', // A darker green for the tag
                padding: '4px 12px',
                borderRadius: '20px',
                alignSelf: 'flex-start',
                marginTop: '10px'
              }}>
                Legume
              </div>
            </div>
          </div>
        </div>

        {/* Content Sections */}
        <div style={{
          display: 'grid',
          gridTemplateColumns: '1fr 1fr',
          gap: '20px',
          padding: '20px'
        }}>
          {/* Cultivation Conditions */}
          <div style={{
            backgroundColor: '#f0fff0', // A very light green
            borderRadius: '12px',
            padding: '20px'
          }}>
            <h2 style={{ fontSize: '20px', margin: '0 0 15px 0', color: '#4d985a' }}>
              <span style={{ marginRight: '10px' }}>üå±</span>
              Condi√ß√µes de Cultivo
            </h2>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
              {condicoesDeCultivo.map((item, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '15px',
                  backgroundColor: '#fff',
                  padding: '10px',
                  borderRadius: '8px',
                  border: '1px solid #e0e0e0'
                }}>
                  <div style={{ fontSize: '24px' }}>{item.icon}</div>
                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    <span style={{ fontWeight: 'bold', fontSize: '14px', color: '#555' }}>{item.label}</span>
                    <span style={{ fontSize: '16px' }}>{item.value}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Care Section */}
          <div style={{
            backgroundColor: '#f0fff0',
            borderRadius: '12px',
            padding: '20px'
          }}>
            <h2 style={{ fontSize: '20px', margin: '0 0 15px 0', color: '#4d985a' }}>
              <span style={{ marginRight: '10px' }}>üíß</span>
              Cuidados
            </h2>
            <div style={{ display: 'flex', flexDirection: 'column', gap: '15px' }}>
              {cuidados.map((item, index) => (
                <div key={index} style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '15px',
                  backgroundColor: '#fff',
                  padding: '10px',
                  borderRadius: '8px',
                  border: '1px solid #e0e0e0'
                }}>
                  <div style={{ fontSize: '24px' }}>{item.icon}</div>
                  <div style={{ display: 'flex', flexDirection: 'column' }}>
                    <span style={{ fontWeight: 'bold', fontSize: '14px', color: '#555' }}>{item.label}</span>
                    <span style={{ fontSize: '16px' }}>{item.value}</span>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Action Buttons */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '15px',
          padding: '20px',
          borderTop: '1px solid #eee'
        }}>
          <button
            onClick={() => navigate(-1)}
            style={{
              padding: '10px 20px',
              borderRadius: '8px',
              border: '1px solid #ccc',
              backgroundColor: '#fff',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            ‚Üê Voltar
          </button>
          <button
            onClick={() => navigate('/')} // Assuming "Fechar" returns to the home page
            style={{
              padding: '10px 20px',
              borderRadius: '8px',
              border: 'none',
              backgroundColor: '#4d985a', // Green color
              color: '#fff',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            ‚ùå Fechar
          </button>
        </div>
      </div>
    </div>
  );
}

