import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BotaoAcao from './BotaoAcao';
import Filtro from './FiltroEnviadas';

function Publicadas({ receitas }) {
  const navigate = useNavigate();
  
  const [filtros, setFiltros] = useState({
    status: '',
    id_categoria: '',
    id_epoca: '',
    id_solo: '',
    id_sol: ''
  });

  const aplicarFiltros = (f) => {
    setFiltros(f);
  };

  const receitasAprovadas = (receitas || []).filter(r => r.status === 'aprovado');

  const receitasFiltradas = receitasAprovadas.filter(r => {
    const filtroCategoria = filtros.id_categoria ? r.id_categoria == filtros.id_categoria : true;
    const filtroEpoca = filtros.id_epoca ? r.id_epoca == filtros.id_epoca : true;
    const filtroSolo = filtros.id_solo ? r.id_solo == filtros.id_solo : true;
    const filtroSol = filtros.id_sol ? r.id_sol == filtros.id_sol : true;

    // A lógica de status é ignorada aqui, pois já filtramos apenas por 'aprovado'
    return filtroCategoria && filtroEpoca && filtroSolo && filtroSol;
  });

  return (
    <div className="enviadas-container flex">
      {/* Coluna de Filtros */}
      <div className="filtros-col">
        <Filtro onFiltrar={aplicarFiltros} />
      </div>

      {/* Lista de Receitas */}
      <div className="receita-lista">
        {receitasFiltradas.length === 0 ? (
          <p className="mensagem-vazia">Nenhuma receita publicada ainda.</p>
        ) : (
          receitasFiltradas.map(r => {
            const dataFormatada = new Date(r.data_publicacao).toLocaleDateString('pt-BR');
            return (
              <div key={r.id || r.id_conteudo} className="receita-card simples">
                <img
                  src={`http://localhost:5000${r.imagens?.[0] || '/uploads/no-image.png'}`}
                  alt={r.nomePlanta}
                  className="receita-imagem"
                  onError={e => e.target.src = 'http://localhost:5000/uploads/no-image.png'}
                />
                <h3>{r.nomePlanta}</h3>
                <p><strong>Autor:</strong> {r.autor || 'Anônimo'}</p>
                <p><strong>Publicado em:</strong> {dataFormatada}</p>
                <BotaoAcao
                  label="Ver receita"
                  tipo="ver"
                  onClick={() => navigate(`/receita/${r.id || r.id_conteudo}`)}
                />
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default Publicadas;
