import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Filtro({ onFiltrar }) {
  const [categorias, setCategorias] = useState([]);
  const [epocas, setEpocas] = useState([]);
  const [solos, setSolos] = useState([]);

  const [categoria, setCategoria] = useState('');
  const [epoca, setEpoca] = useState('');
  const [solo, setSolo] = useState('');

  const [openSection, setOpenSection] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/filtros/categorias').then(r => setCategorias(r.data));
    axios.get('http://localhost:5000/api/filtros/epocas').then(r => setEpocas(r.data));
    axios.get('http://localhost:5000/api/filtros/solos').then(r => setSolos(r.data));
  }, []);

  const toggleSection = (section) => {
    setOpenSection(openSection === section ? null : section);
  };

  const aplicarFiltro = () => {
    onFiltrar({ categoria, epoca, solo });
  };

  return (
    <div className="filtros">
      <h3>Filtros</h3>

      {/* Categoria */}
      <div className="filtro-bloco">
        <div className="filtro-header" onClick={() => toggleSection('categoria')}>
          <span>Categoria</span>
          <span>{openSection === 'categoria' ? '▲' : '▼'}</span>
        </div>
        {openSection === 'categoria' && (
          <select value={categoria} onChange={e => setCategoria(e.target.value)}>
            <option value="">Todas</option>
            {categorias.map(c => (
              <option key={c.id} value={c.id}>{c.nome}</option>
            ))}
          </select>
        )}
      </div>

      {/* Época */}
      <div className="filtro-bloco">
        <div className="filtro-header" onClick={() => toggleSection('epoca')}>
          <span>Época</span>
          <span>{openSection === 'epoca' ? '▲' : '▼'}</span>
        </div>
        {openSection === 'epoca' && (
          <select value={epoca} onChange={e => setEpoca(e.target.value)}>
            <option value="">Todas</option>
            {epocas.map(ei => (
              <option key={ei.id} value={ei.id}>{ei.nome}</option>
            ))}
          </select>
        )}
      </div>

      {/* Solo */}
      <div className="filtro-bloco">
        <div className="filtro-header" onClick={() => toggleSection('solo')}>
          <span>Solo</span>
          <span>{openSection === 'solo' ? '▲' : '▼'}</span>
        </div>
        {openSection === 'solo' && (
          <select value={solo} onChange={e => setSolo(e.target.value)}>
            <option value="">Todos</option>
            {solos.map(s => (
              <option key={s.id} value={s.id}>{s.nome}</option>
            ))}
          </select>
        )}
      </div>

      <button onClick={aplicarFiltro} className="btn-filtrar">Aplicar</button>
    </div>
  );
}

export default Filtro;
