function BotaoAcao({ label, tipo = "padrao", onClick }) {
  const baseStyle = {
    padding: "0.5rem 1rem",
    borderRadius: "0.5rem",
    fontSize: "0.875rem",
    fontWeight: "600",
    transition: "all 0.2s ease-in-out",
    cursor: "pointer",
    border: "none",
  };

  const estilos = {
    ver: {
      backgroundColor: "#22c55e", // verde
      color: "white",
    },
    aprovar: {
      backgroundColor: "#3b82f6", // azul
      color: "white",
    },
    rejeitar: {
      backgroundColor: "#facc15", // amarelo
      color: "black",
    },
    excluir: {
      backgroundColor: "#dc2626", // vermelho
      color: "white",
    },
    padrao: {
      backgroundColor: "#6b7280", // cinza
      color: "white",
    },
  };

  const hoverEstilos = {
    ver: { backgroundColor: "#16a34a" },
    aprovar: { backgroundColor: "#2563eb" },
    rejeitar: { backgroundColor: "#eab308" },
    excluir: { backgroundColor: "#b91c1c" },
    padrao: { backgroundColor: "#4b5563" },
  };

  const estiloAtual = estilos[tipo] || estilos.padrao;
  const estiloHover = hoverEstilos[tipo] || hoverEstilos.padrao;

  return (
    <button
      style={estiloAtual}
      onClick={onClick}
      onMouseEnter={(e) => {
        Object.assign(e.target.style, { ...baseStyle, ...estiloHover });
      }}
      onMouseLeave={(e) => {
        Object.assign(e.target.style, { ...baseStyle, ...estiloAtual });
      }}
    >
      {label}
    </button>
  );
}

export default BotaoAcao;
