import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Swal from 'sweetalert2';
import Topo from '../components/Topo';
import BotaoAcao from '../components/BotaoAcao';
import '../styles/style.css';

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [receita, setReceita] = useState(null);
  const [favoritado, setFavoritado] = useState(false);

  const user = JSON.parse(localStorage.getItem('user'));
  const idUsuario = user?.id;

  useEffect(() => {
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => setReceita(res.data))
      .catch(err => console.error('Erro ao buscar receita:', err));
  }, [id]);

  useEffect(() => {
    if (idUsuario) {
      axios.get(`http://localhost:5000/api/favoritos/${idUsuario}/${id}`)
        .then(res => setFavoritado(res.data?.statusAtivo === 1))
        .catch(err => console.error('Erro ao verificar favorito:', err));
    }
  }, [id, idUsuario]);

  const toggleFavorito = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/favoritos/${idUsuario}/${id}`);
      const favorito = response.data;

      if (!favorito) {
        await axios.post(`http://localhost:5000/api/favoritos`, {
          id_usuario: idUsuario,
          id_conteudo: id,
          statusAtivo: 1
        });
        setFavoritado(true);
      } else if (favorito.statusAtivo === 1) {
        await axios.put(`http://localhost:5000/api/favoritos/remover`, {
          id_usuario: idUsuario,
          id_conteudo: id
        });
        setFavoritado(false);
      } else {
        await axios.post(`http://localhost:5000/api/favoritos`, {
          id_usuario: idUsuario,
          id_conteudo: id,
          statusAtivo: 1
        });
        setFavoritado(true);
      }
    } catch (err) {
      console.error('Erro ao atualizar favorito:', err);
    }
  };

  if (!receita) return <div>Carregando receita...</div>;

  const dataFormatada = new Date(receita.data_publicacao).toLocaleDateString('pt-BR');

  const styles = {
    container: {
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      minHeight: '100vh',
      padding: '20px',
      backgroundColor: '#f5f5f5'
    },
    card: {
      width: '100%',
      maxWidth: '800px',
      backgroundColor: '#fff',
      borderRadius: '16px',
      boxShadow: '0 4px 20px rgba(0,0,0,0.1)',
      overflow: 'hidden',
      fontFamily: 'sans-serif',
      marginTop: '20px'
    },
    header: {
      position: 'relative',
      backgroundColor: '#2d6a4f',
      padding: '20px',
      color: '#fff',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'flex-start',
      borderRadius: '16px 16px 0 0'
    },
    favBtn: {
      position: 'absolute',
      top: '15px',
      right: '20px',
      cursor: 'pointer',
      zIndex: 10
    },
    headerContent: {
      display: 'flex',
      alignItems: 'center',
      width: '100%',
      gap: '20px'
    },
    imagem: {
      width: '120px',
      height: '120px',
      borderRadius: '8px',
      overflow: 'hidden',
      flexShrink: 0
    },
    categoria: {
      fontSize: '14px',
      backgroundColor: '#2d6a4f',
      padding: '4px 12px',
      borderRadius: '20px',
      alignSelf: 'flex-start',
      marginTop: '10px'
    },
    section: {
      backgroundColor: '#f0fff0',
      borderRadius: '12px',
      padding: '20px'
    },
    sectionTitle: {
      fontSize: '20px',
      margin: '0 0 15px 0',
      color: '#4d985a',
      display: 'flex',
      alignItems: 'center',
      gap: '10px'
    },
    infoCard: {
      display: 'flex',
      alignItems: 'center',
      gap: '15px',
      backgroundColor: '#fff',
      padding: '10px',
      borderRadius: '8px',
      border: '1px solid #e0e0e0'
    },
    actions: {
      display: 'flex',
      justifyContent: 'center',
      gap: '15px',
      padding: '20px',
      borderTop: '1px solid #eee'
    }
  };

  return (
    <div style={styles.container}>
      <Topo />

      <div style={styles.card}>
        {/* CabeÃ§alho */}
        <div style={styles.header}>
          <div
            onClick={toggleFavorito}
            title={favoritado ? 'Remover dos favoritos' : 'Adicionar aos favoritos'}
