import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './style.css';

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [receita, setReceita] = useState(null);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => setReceita(res.data))
      .catch(err => {
        console.error('Erro ao buscar receita:', err);
        navigate('/conteudo'); // redireciona se der erro
      });
  }, [id, navigate]);

  if (!receita) return <p>Carregando...</p>;

  return (
    <div className="receita-detalhe">
      <button onClick={() => navigate('/conteudo')} className="btn-voltar">Voltar</button>
      <h2>{receita.nomePlanta}</h2>
      {receita.imagens?.length > 0 && (
        <img src={`http://localhost:5000/uploads/${receita.imagens[0]}`} alt={receita.nomePlanta} className="receita-detalhe-img" />
      )}
      <p><strong>Categoria:</strong> {receita.categoria}</p>
      <p><strong>Plantio:</strong> {receita.epoca}</p>
      <p><strong>Temperatura:</strong> {receita.temperatura}</p>
      <p><strong>Solo:</strong> {receita.solo}</p>
      <p><strong>Rega:</strong> {receita.rega}</p>
      <p><strong>Sol:</strong> {receita.sol}</p>
      <p><strong>Instruções:</strong> {receita.instrucoes}</p>
      <small>Por {receita.autor || 'Anônimo'} em {new Date(receita.data_publicacao).toLocaleDateString()}</small>
    </div>
  );
}

export default ReceitaDetalhe;
