import React from "react";
import Swal from "sweetalert2";

function BotaoAcao({ label, tipo = "padrao", onClick }) {
  // Mapeamento de tipos para classes CSS
  const estilos = {
    ver: "btn-verde",
    aprovar: "btn-aprovar",
    rejeitar: "btn-rejeitar",
    excluir: "btn-excluir",
    voltar: "btn-voltar",
    padrao: "btn-cinza",
  };

  const handleClick = () => {
    onClick?.();

    // Evita popup para Voltar ou Ver
    if (label.includes("Voltar") || label.includes("Ver")) return;

    Swal.fire({
      icon: "success",
      title: "Alteração realizada!",
      text: `Ação "${label}" concluída com sucesso.`,
      showConfirmButton: true,
    });
  };

  return (
    <button
      className={estilos[tipo] || estilos.padrao}
      onClick={handleClick}
    >
      {label}
    </button>
  );
}

export default BotaoAcao;
