import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './style.css';

function Home() {
  const [menuAberto, setMenuAberto] = useState(false);
  const [notificacoes, setNotificacoes] = useState([]);
  const navigate = useNavigate();

  // ID fixo para exemplo — idealmente isso vem do login
  const idUsuario = 1;

  useEffect(() => {
    axios.get(`http://localhost:5000/api/notificacoes/${idUsuario}`)
      .then(res => {
        const novas = res.data.filter(n => !n.visualizado);
        setNotificacoes(novas);
      })
      .catch(err => console.error('Erro ao buscar notificações:', err));
  }, []);

  const deslogar = () => {
    navigate('/');
  };

  return (
    <div className="home-container">
      {/* TOPO - Menu + Logo + Notificação */}
      <div className="top-bar">
        <img
          src="/menu.png"
          alt="Menu"
          className="menu-icon"
          onClick={() => setMenuAberto(!menuAberto)}
        />

        <img src="/logo.png" alt="Logo AgroPlanner" className="logo-topo" />

        <div className="notificacao-icone">
          <img
            src={notificacoes.length > 0 ? "/notification-yes.png" : "/notification-no.png"}
            alt="Notificações"
            className="notificacao-img"
          />
          {notificacoes.length > 0 && (
            <span className="notificacao-contador">{notificacoes.length}</span>
          )}
        </div>
      </div>

      {menuAberto && (
        <div className="menu-lateral">
          <button onClick={deslogar}>Sair</button>
        </div>
      )}

      {/* Conteúdo principal */}
      <div className="home-card">
        <Link to="/forum" className="home-link">
          <h3>FÓRUM</h3>
          <p>Venha conversar com colegas de plantio.</p>
        </Link>
        <Link to="/calendario" className="home-link">
          <h3>Calendário</h3>
          <p>Verifique as datas!!</p>
        </Link>
        <Link to="/conteudo" className="home-link">
          <h3>Conteúdo</h3>
          <p>Venha ver receitas para você plantar</p>
        </Link>
      </div>

      <p className="duvida">Não encontrou o que gostaria?</p>
      <button className="fale-conosco">Fale conosco</button>
    </div>
  );  
}

export default Home;
