import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import Topo from '../components/Topo';

// √çcones simples
const IconePerfil = () => <span style={{ fontSize: '1.2em' }}>üë§</span>;
const IconeDocumento = () => <span style={{ fontSize: '1.2em' }}>üìÑ</span>;
const IconeConfig = () => <span style={{ fontSize: '1.2em' }}>‚öôÔ∏è</span>;
const IconeLogin = () => <span style={{ fontSize: '1.2em' }}>üîë</span>;
const IconeFavoritos = () => <span style={{ fontSize: '1.5rem', color: '#ff6961' }}>‚ù§</span>;
const IconePostagens = () => <span style={{ fontSize: '1.5rem', color: '#2c7be5' }}>‚úçÔ∏è</span>;

/* ===== Modal reutiliz√°vel ===== */
function Modal({ title, open, onClose, children, onSubmit, submitText = 'Salvar' }) {
  if (!open) return null;
  return (
    <div style={m.overlay} onClick={onClose}>
      <div style={m.container} onClick={(e) => e.stopPropagation()}>
        <div style={m.header}>
          <h3 style={{ margin: 0 }}>{title}</h3>
          <button onClick={onClose} style={m.closeBtn}>√ó</button>
        </div>
        <div style={m.body}>{children}</div>
        <div style={m.footer}>
          <button onClick={onClose} style={m.cancel}>Cancelar</button>
          <button onClick={onSubmit} style={m.save}>{submitText}</button>
        </div>
      </div>
    </div>
  );
}

function Perfil() {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [cargo, setCargo] = useState('');
  const [status, setStatus] = useState('');
  const [fotoPerfil, setFotoPerfil] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [totalFavoritos, setTotalFavoritos] = useState(0);
  const [totalPostagens, setTotalPostagens] = useState(0);
  const [erroApi, setErroApi] = useState('');
  const [carregando, setCarregando] = useState(false);

  // modais
  const [openEmail, setOpenEmail] = useState(false);
  const [openTelefone, setOpenTelefone] = useState(false);
  const [openEndereco, setOpenEndereco] = useState(false);

  // campos do modal telefone
  const [numeroCelular, setNumeroCelular] = useState('');

  // campos do modal endere√ßo
  const [enderecoRua, setEnderecoRua] = useState('');
  const [enderecoNum, setEnderecoNum] = useState('');
  const [enderecoBairro, setEnderecoBairro] = useState('');
  const [enderecoCep, setEnderecoCep] = useState('');

  const navigate = useNavigate();
  const API_BASE = useMemo(() => process.env.REACT_APP_API_BASE || 'http://localhost:5000', []);

  const atividadesRecentes = [
    { id: 1, texto: 'Perfil atualizado', tipo: 'profile', icone: <IconePerfil /> },
    { id: 2, texto: 'Documento enviado', tipo: 'document', icone: <IconeDocumento /> },
    { id: 3, texto: 'Configura√ß√µes alteradas', tipo: 'settings', icone: <IconeConfig /> },
    { id: 4, texto: 'Login realizado', tipo: 'login', icone: <IconeLogin /> },
  ];

  // helper imagem
  const resolveImgSrc = (valor) => {
    if (!valor) return `${API_BASE}/uploads/no-image.png`;
    if (/^https?:\/\//i.test(valor)) return valor;
    return `${API_BASE}${valor}`;
  };

  // carrega info do localStorage e contagens
  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user || !user.id) {
      navigate('/');
      return;
    }
    const idUsuario = user.id;

    setNome(user.nome || 'Nome do Usu√°rio');
    setEmail(user.email || '');
    setCargo(user.role || user.tipo || 'Usu√°rio Padr√£o');
    setStatus(user.status || 'Ativo');
    setFotoPerfil(user.foto_perfil || '');

    // pr√©-carregar telefone/endere√ßo (se tiver no LS)
    setNumeroCelular(user.numero_celular || '');
    setEnderecoRua(user.endereco_rua || '');
    setEnderecoNum(user.endereco_num || '');
    setEnderecoBairro(user.endereco_bairro || '');
    setEnderecoCep(user.endereco_cep || '');

    const buscarDadosDoBackend = async () => {
      setErroApi('');
      setCarregando(true);
      const ctrl = new AbortController();
      try {
        const [favoritosRes, postagensRes] = await Promise.all([
          fetch(`${API_BASE}/api/favoritos/contagem/${idUsuario}`, { signal: ctrl.signal }),
          fetch(`${API_BASE}/api/conteudos/contagem/${idUsuario}`, { signal: ctrl.signal }),
        ]);
        if (!favoritosRes.ok) throw new Error(await favoritosRes.text());
        if (!postagensRes.ok) throw new Error(await postagensRes.text());

        const favoritosData = await favoritosRes.json();
        const postagensData = await postagensRes.json();
        setTotalFavoritos(Number(favoritosData?.totalFavoritos) || 0);
        setTotalPostagens(Number(postagensData?.totalPostagens) || 0);
      } catch (err) {
        console.error(err);
        setErroApi('N√£o foi poss√≠vel conectar ao servidor.');
      } finally {
        setCarregando(false);
      }
      return () => ctrl.abort();
    };
    buscarDadosDoBackend();
  }, [navigate, API_BASE]);

  const salvarNoLocalStorage = (patch) => {
    const atual = JSON.parse(localStorage.getItem('user')) || {};
    const novo = { ...atual, ...patch };
    localStorage.setItem('user', JSON.stringify(novo));
  };

  /* ========= a√ß√µes dos modais ========= */

  const submitEmail = async () => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user?.id) return;
    try {
      const r = await fetch(`${API_BASE}/api/users/${user.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });
      if (!r.ok) throw new Error(await r.text());
      salvarNoLocalStorage({ email });
      setOpenEmail(false);
      setMensagem('E-mail atualizado!');
      setTimeout(() => setMensagem(''), 2500);
    } catch (e) {
      console.error(e);
      setErroApi('Falha ao atualizar e-mail');
      setTimeout(() => setErroApi(''), 3000);
    }
  };

  const submitTelefone = async () => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user?.id) return;
    try {
      const r = await fetch(`${API_BASE}/api/users/${user.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ numero_celular: numeroCelular }),
      });
      if (!r.ok) throw new Error(await r.text());
      salvarNoLocalStorage({ numero_celular: numeroCelular });
      setOpenTelefone(false);
      setMensagem('Telefone atualizado!');
      setTimeout(() => setMensagem(''), 2500);
    } catch (e) {
      console.error(e);
      setErroApi('Falha ao atualizar telefone');
      setTimeout(() => setErroApi(''), 3000);
    }
  };

  const submitEndereco = async () => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (!user?.id) return;
    try {
      const payload = {
        endereco_rua: enderecoRua || null,
        endereco_num: enderecoNum || null,
        endereco_bairro: enderecoBairro || null,
        endereco_cep: enderecoCep || null,
      };
      const r = await fetch(`${API_BASE}/api/users/${user.id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload),
      });
      if (!r.ok) throw new Error(await r.text());
      salvarNoLocalStorage({
        endereco_rua: enderecoRua,
        endereco_num: enderecoNum,
        endereco_bairro: enderecoBairro,
        endereco_cep: enderecoCep,
      });
      setOpenEndereco(false);
      setMensagem('Endere√ßo atualizado!');
      setTimeout(() => setMensagem(''), 2500);
    } catch (e) {
      console.error(e);
      setErroApi('Falha ao atualizar endere√ßo');
      setTimeout(() => setErroApi(''), 3000);
    }
  };

  const deslogar = () => {
    localStorage.clear();
    navigate('/');
  };

  const Tag = ({ text, color, bgColor }) => (
    <span
      style={{
        padding: '4px 8px',
        borderRadius: '5px',
        backgroundColor: bgColor || '#e0e0e0',
        color: color || '#333',
        fontSize: '0.8em',
        fontWeight: 'bold',
        marginRight: '5px',
      }}
    >
      {text}
    </span>
  );

  const avatarSrc = resolveImgSrc(fotoPerfil);

  return (
    <div style={styles.pagina}>
      <Topo />
      <div style={styles.mainContent}>
        <div style={styles.leftColumn}>
          <div style={styles.profileCard}>
            <img src={avatarSrc} alt="Avatar do Usu√°rio" style={styles.avatar} />

            <h2 style={styles.userName}>{nome}</h2>

            <div style={styles.tagsContainer}>
              <Tag text={cargo} bgColor="#e7f0ff" color="#2c7be5" />
              <Tag text={status} bgColor="#d4edda" color="#28a745" />
            </div>

            {/* √çcones que abrem os modais */}
            <div style={styles.contactIcons}>
              <span title="Editar e-mail" style={styles.iconItem} onClick={() => setOpenEmail(true)}>‚úâÔ∏è</span>
              <span title="Editar telefone" style={styles.iconItem} onClick={() => setOpenTelefone(true)}>üìû</span>
              <span title="Editar endere√ßo" style={styles.iconItem} onClick={() => setOpenEndereco(true)}>üìç</span>
            </div>

            <div style={styles.profileButtons}>
              <button style={styles.editButton} onClick={() => setMensagem('Use os √≠cones para editar itens r√°pidos üòâ')}>
                ‚úèÔ∏è Editar Perfil
              </button>
              <button style={styles.settingsButton}>‚öôÔ∏è</button>
            </div>
            <button onClick={deslogar} style={styles.logoutButton}>Sair</button>

            {mensagem && <p style={styles.mensagemSucesso}>{mensagem}</p>}
            {erroApi && <p style={styles.mensagemErro}>{erroApi}</p>}
          </div>
        </div>

        <div style={styles.rightColumn}>
          <div style={styles.statsGrid}>
            <div style={styles.statCard}>
              <div style={styles.statInfo}>
                <span style={styles.statNumber}>{carregando ? '...' : totalFavoritos}</span>
                <span style={styles.statLabel}>Postagens favoritadas</span>
              </div>
              <IconeFavoritos />
            </div>
            <div style={styles.statCard}>
              <div style={styles.statInfo}>
                <span style={styles.statNumber}>{carregando ? '...' : totalPostagens}</span>
                <span style={styles.statLabel}>Postagens feitas</span>
              </div>
              <IconePostagens />
            </div>
          </div>

          <div style={styles.activitiesCard}>
            <h3 style={styles.activitiesTitle}>Atividades Recentes</h3>
            {atividadesRecentes.map((activity, index) => (
              <div
                key={activity.id}
                style={{
                  ...styles.activityItem,
                  borderBottom: index === atividadesRecentes.length - 1 ? 'none' : '1px solid #eee',
                }}
              >
                <div style={styles.activityIcon}>{activity.icone}</div>
                <span style={styles.activityText}>{activity.texto}</span>
                <span style={{
                  padding: '4px 8px',
                  borderRadius: '5px',
                  fontSize: '0.8em',
                  fontWeight: 'bold',
                  backgroundColor:
                    activity.tipo === 'profile' ? '#ffe0b2'
                      : activity.tipo === 'document' ? '#c8e6c9'
                      : activity.tipo === 'settings' ? '#e1bee7'
                      : '#bbdefb',
                  color:
                    activity.tipo === 'profile' ? '#e65100'
                      : activity.tipo === 'document' ? '#2e7d32'
                      : activity.tipo === 'settings' ? '#6a1b9a'
                      : '#1565c0'
                }}>{activity.tipo}</span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* MODAL E-MAIL */}
      <Modal
        title="Editar e-mail"
        open={openEmail}
        onClose={() => setOpenEmail(false)}
        onSubmit={submitEmail}
        submitText="Salvar e-mail"
      >
        <label style={styles.labelInput}>E-mail</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={styles.inputField}
          placeholder="ex: joao@email.com"
        />
      </Modal>

      {/* MODAL TELEFONE */}
      <Modal
        title="Editar telefone"
        open={openTelefone}
        onClose={() => setOpenTelefone(false)}
        onSubmit={submitTelefone}
        submitText="Salvar telefone"
      >
        <label style={styles.labelInput}>N√∫mero de celular</label>
        <input
          type="tel"
          value={numeroCelular}
          onChange={(e) => setNumeroCelular(e.target.value)}
          style={styles.inputField}
          placeholder="(11) 9 9999-9999"
        />
      </Modal>

      {/* MODAL ENDERE√áO */}
      <Modal
        title="Editar endere√ßo"
        open={openEndereco}
        onClose={() => setOpenEndereco(false)}
        onSubmit={submitEndereco}
        submitText="Salvar endere√ßo"
      >
        <div style={{ display: 'grid', gap: 10 }}>
          <div>
            <label style={styles.labelInput}>Rua</label>
            <input
              type="text"
              value={enderecoRua}
              onChange={(e) => setEnderecoRua(e.target.value)}
              style={styles.inputField}
              placeholder="Ex.: Av. Paulista"
            />
          </div>
          <div>
            <label style={styles.labelInput}>N√∫mero</label>
            <input
              type="text"
              value={enderecoNum}
              onChange={(e) => setEnderecoNum(e.target.value)}
              style={styles.inputField}
              placeholder="123"
            />
          </div>
          <div>
            <label style={styles.labelInput}>Bairro</label>
            <input
              type="text"
              value={enderecoBairro}
              onChange={(e) => setEnderecoBairro(e.target.value)}
              style={styles.inputField}
              placeholder="Centro"
            />
          </div>
          <div>
            <label style={styles.labelInput}>CEP</label>
            <input
              type="text"
              value={enderecoCep}
              onChange={(e) => setEnderecoCep(e.target.value)}
              style={styles.inputField}
              placeholder="00000-000"
            />
          </div>
        </div>
      </Modal>
    </div>
  );
}

