import React from "react";
import Swal from "sweetalert2";

function BotaoAcao({ label, tipo = "padrao", onClick }) {
  const baseClasses =
    "px-4 py-2 rounded-lg text-sm font-medium transition-colors duration-200 shadow-md";

  // ðŸŽ¨ Paleta diferenciada por tipo
  const estilos = {
    ver: "bg-emerald-500 text-white hover:bg-emerald-600", // Verde claro
    aprovar: "bg-blue-500 text-white hover:bg-blue-600",   // Azul
    rejeitar: "bg-orange-400 text-white hover:bg-orange-500", // Laranja
    excluir: "bg-red-600 text-white hover:bg-red-700",     // Vermelho escuro
    voltar: "bg-gray-400 text-black hover:bg-gray-500",    // Cinza
    padrao: "bg-gray-300 text-black hover:bg-gray-400",    // Neutro
  };

  const handleClick = () => {
    // Executa aÃ§Ã£o principal
    onClick?.();

    // ðŸš« Sem popup para "Voltar" ou "Ver Receita"
    if (tipo === "voltar" || tipo === "ver") return;

    // âœ… Mostra popup para os outros botÃµes
    Swal.fire({
      icon: "success",
      title: "AlteraÃ§Ã£o realizada!",
      text: `AÃ§Ã£o "${label}" concluÃ­da com sucesso.`,
      showConfirmButton: true,
    });
  };

  return (
    <button
      className={`${baseClasses} ${estilos[tipo] || estilos.padrao}`}
      onClick={handleClick}
    >
      {label}
    </button>
  );
}

export default BotaoAcao;
