import React from 'react';

function BotaoAcao({ label, onClick, tipo = 'padrao' }) {
  const estilos = {
    padrao: {
      backgroundColor: '#2d6a4f',
      color: '#fff',
      border: 'none',
      padding: '10px 20px',
      borderRadius: '8px',
      cursor: 'pointer',
      fontSize: '16px',
      fontWeight: 'bold',
      transition: '0.3s'
    }
  };

  const estilo = estilos[tipo] || estilos.padrao;

  return (
    <button
      onClick={onClick}
      style={estilo}
      onMouseOver={(e) => e.target.style.backgroundColor = '#1b4332'}
      onMouseOut={(e) => e.target.style.backgroundColor = '#2d6a4f'}
    >
      {label}
    </button>
  );
}

export default BotaoAcao;
