/**
 * Filtra receitas enviadas por um usuário específico,
 * excluindo as rejeitadas e inativas.
 */
export function filtrarReceitasEnviadas(receitas, idUsuario) {
  return receitas.filter(receita =>
    receita.id_autor === idUsuario &&
    receita.statusAtivo !== 0 &&
    receita.status !== 'rejeitado'
  );
}

/**
 * Ordena uma lista de receitas pela data de publicação.
 * @param {Array} lista - Lista de receitas
 * @param {'asc' | 'desc'} ordem - 'asc' para mais antigas, 'desc' para mais recentes
 */
export function ordenarPorData(lista, ordem = 'desc') {
  return [...lista].sort((a, b) => {
    const dataA = new Date(a.fecha_publicacion);
    const dataB = new Date(b.fecha_publicacion);
    return ordem === 'asc' ? dataA - dataB : dataB - dataA;
  });
}
