import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import BotaoAcao from './BotaoAcao';
import Filtro from './FiltroEnviadas';

function Publicadas({ receitas }) {
  const navigate = useNavigate();
  
  const [filtros, setFiltros] = useState({
    status: '',
    id_categoria: '',
    id_epoca: '',
    id_solo: '',
    id_sol: ''
  });

  const aplicarFiltros = (f) => {
    setFiltros(f);
  };

  const receitasAprovadas = (receitas || []).filter(r => r.status === 'aprovado');

  const receitasFiltradas = receitasAprovadas.filter(r => {
    const filtroCategoria = filtros.id_categoria ? r.id_categoria == filtros.id_categoria : true;
    const filtroEpoca = filtros.id_epoca ? r.id_epoca == filtros.id_epoca : true;
    const filtroSolo = filtros.id_solo ? r.id_solo == filtros.id_solo : true;
    const filtroSol = filtros.id_sol ? r.id_sol == filtros.id_sol : true;

    return filtroCategoria && filtroEpoca && filtroSolo && filtroSol;
  });

  const styles = {
    container: {
      display: 'flex',
      gap: '20px',
      padding: '20px',
      backgroundColor: '#f0f4f7',
      minHeight: '100vh',
      fontFamily: 'Arial, sans-serif',
    },
    filtrosCol: {
      flexShrink: 0,
      width: '250px',
    },
    receitaLista: {
      flexGrow: 1,
      display: 'flex',
      flexDirection: 'column',
      gap: '20px',
    },
    receitaCard: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.08)',
      overflow: 'hidden',
      display: 'flex',
      flexDirection: 'column',
      minHeight: '220px',
      maxWidth: '400px',
      padding: '0',
    },
    receitaImagemContainer: {
      width: '100%',
      height: '180px',
      overflow: 'hidden',
      backgroundColor: '#2d6a4f',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      flexShrink: 0,
      flexDirection: 'column',
      color: '#fff',
      fontWeight: 600,
      textAlign: 'center',
    },
    receitaImagem: {
      width: '100%',
      height: '100%',
      objectFit: 'cover',
    },
    noImageIcon: {
      width: '40px',
      height: '40px',
      marginBottom: '10px',
    },
    receitaInfo: {
      padding: '15px',
      display: 'flex',
      flexDirection: 'column',
      flexGrow: 1,
    },
    receitaId: {
      fontSize: '1rem',
      fontWeight: 'bold',
      color: '#333',
      marginBottom: '8px',
    },
    receitaAutor: {
      fontSize: '0.9rem',
      color: '#666',
      display: 'flex',
      alignItems: 'center',
      gap: '8px',
      marginBottom: '8px',
    },
    autorIcon: {
      width: '16px',
      height: '16px',
    },
    receitaData: {
      fontSize: '0.9rem',
      color: '#666',
      marginBottom: '15px',
    },
    mensagemVazia: {
      textAlign: 'center',
      color: '#777',
      fontSize: '1.2rem',
      marginTop: '50px',
      width: '100%',
    },
    emptyStateContainer: {
      backgroundColor: '#fff',
      borderRadius: '12px',
      boxShadow: '0 4px 12px rgba(0, 0, 0, 0.05)',
      padding: '40px 20px',
      textAlign: 'center',
      maxWidth: '600px',
      margin: '80px auto',
    },
    emptyStateIconWrapper: {
      width: '80px',
      height: '80px',
      margin: '0 auto 20px',
      border: '4px solid #b0c9b0',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    },
    emptyStateIcon: {
      width: '50px',
      height: '50px',
    },
    emptyStateTitle: {
      fontSize: '1.5rem',
      fontWeight: 600,
      color: '#4a4a4a',
      marginBottom: '10px',
    },
    emptyStateText: {
      fontSize: '1rem',
      color: '#777',
      maxWidth: '400px',
      margin: '0 auto',
    },
  };

  return (
    <div style={styles.container}>
      <div style={styles.filtrosCol}>
        <Filtro onFiltrar={aplicarFiltros} />
      </div>

      <div style={styles.receitaLista}>
        {receitasFiltradas.length === 0 ? (
          <div style={styles.emptyStateContainer}>
            <div style={styles.emptyStateIconWrapper}>
              <img src="/exclamation-mark.png" alt="Ícone de exclamação" style={styles.emptyStateIcon} />
            </div>
            <h3 style={styles.emptyStateTitle}>Nenhuma receita publicada ainda.</h3>
            <p style={styles.emptyStateText}>
              Receitas aprovadas aparecerão aqui para o público em geral.
            </p>
          </div>
        ) : (
          receitasFiltradas.map(r => {
            const dataFormatada = new Date(r.data_publicacao).toLocaleDateString('pt-BR');
            return (
              <div key={r.id || r.id_conteudo} style={styles.receitaCard}>
                <div style={styles.receitaImagemContainer}>
                  {r.imagens?.[0] ? (
                    <img
                      src={`http://localhost:5000${r.imagens[0]}`}
                      alt={r.nomePlanta}
                      style={styles.receitaImagem}
                      onError={e => e.target.src = 'http://localhost:5000/uploads/no-image.png'}
                    />
                  ) : (
                    <>
                      <img src="/exclamation-mark.png" alt="Sem imagem" style={styles.noImageIcon} />
                      <p>Sem Imagem</p>
                    </>
                  )}
                </div>
                <div style={styles.receitaInfo}>
                  <div style={styles.receitaId}>{r.id || r.id_conteudo}</div>
                  <p style={styles.receitaAutor}>
                    <img src="/user.png" alt="Ícone de usuário" style={styles.autorIcon} />
                    {r.autor || 'Anônimo'}
                  </p>
                  <p style={styles.receitaData}>
                    Publicado em: {dataFormatada}
                  </p>
                  <div style={{ marginTop: 'auto' }}>
                    <BotaoAcao
                      label="Ver receita"
                      tipo="ver"
                      onClick={() => navigate(`/receita/${r.id || r.id_conteudo}`)}
                    />
                  </div>
                </div>
              </div>
            );
          })
        )}
      </div>
    </div>
  );
}

export default Publicadas;
