import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import Topo from './Topo';

function ReceitaDetalhe() {
  const { id } = useParams();
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user'));
  const idUsuario = user?.id;

  const [receita, setReceita] = useState(null);
  const [favorito, setFavorito] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:5000/api/conteudos/${id}`)
      .then(res => {
        setReceita(res.data);
        setFavorito(res.data.favorita); // se já está favorita
      })
      .catch(err => console.error('Erro ao carregar receita:', err));
  }, [id]);

  const alternarFavorito = () => {
    axios.post(`http://localhost:5000/api/conteudos/${id}/favorito`, { idUsuario })
      .then(() => setFavorito(prev => !prev))
      .catch(err => console.error('Erro ao atualizar favorito:', err));
  };

  if (!receita) return <p>Carregando...</p>;

  return (
    <div>
      <Topo comMenu />
      <div className="receita-detalhe">
        <h1>{receita.nomePlanta}</h1>
        <img
          src={receita.imagens?.[0] ? `http://localhost:5000${receita.imagens[0]}` : '/no-image.png'}
          alt={receita.nomePlanta}
          className="img-detalhe"
        />
        <button onClick={alternarFavorito} className="estrela-favorito">
          {favorito ? '⭐' : '☆'}
        </button>
        <p><strong>Categoria:</strong> {receita.categoria}</p>
        <p><strong>Época:</strong> {receita.epoca}</p>
        <p><strong>Temperatura:</strong> {receita.temperatura}</p>
        <p><strong>Solo:</strong> {receita.solo}</p>
        <p><strong>Rega:</strong> {receita.rega}</p>
        <p><strong>Sol:</strong> {receita.sol}</p>
        <p><strong>Instruções:</strong> {receita.instrucoes}</p>
        <button onClick={() => navigate(-1)} className="botao-voltar">Voltar</button>
      </div>
    </div>
  );
}

export default ReceitaDetalhe;
